div.script:
function googleTranslateElementInit2() {
    new google.translate.TranslateElement({
        pageLanguage: 'en',
        autoDisplay: false
    }, 'google_translate_element2');
}

function GTranslateGetCurrentLang() {
    var keyValue = document['cookie'].match('(^|;) ?googtrans=([^;]*)(;|$)');
    return keyValue ? keyValue[2].split('/')[2] : null;
}

function GTranslateFireEvent(element, event) {
    try {
        if (document.createEventObject) {
            var evt = document.createEventObject();
            element.fireEvent('on' + event, evt)
        } else {
            var evt = document.createEvent('HTMLEvents');
            evt.initEvent(event, true, true);
            element.dispatchEvent(evt)
        }
    } catch (e) {}
}

function doGTranslate(lang_pair) {
    if (lang_pair.value) lang_pair = lang_pair.value;
    if (lang_pair == '') return;
    var lang = lang_pair.split('|')[1];
    if (GTranslateGetCurrentLang() == null && lang == lang_pair.split('|')[0]) return;
    if(typeof gtag != 'undefined'){
    	gtag('event', lang, {
    		'event_category': 'Translation',
    		'value': location.hostname + location.pathname + location.search
    	});
    }
    else if (typeof ga != 'undefined') {
        ga('send', 'event', 'GTranslate', lang, location.hostname + location.pathname + location.search);
    } else {
        if (typeof _gaq != 'undefined'){ 
        	_gaq.push(['_trackEvent', 'GTranslate', lang, location.hostname + location.pathname + location.search]);
        }
    }
    var teCombo;
    var sel = document.getElementsByTagName('select');
    for (var i = 0; i < sel.length; i++)
        if (/goog-te-combo/.test(sel[i].className)) {
            teCombo = sel[i];
            break;
        }
    if (document.getElementById('google_translate_element2') == null || document.getElementById('google_translate_element2').innerHTML.length == 0 || teCombo.length == 0 || teCombo.innerHTML.length == 0) {
        setTimeout(function() {
            doGTranslate(lang_pair)
        }, 500)
    } else {
        teCombo.value = lang;
        GTranslateFireEvent(teCombo, 'change');
        GTranslateFireEvent(teCombo, 'change')
    }
}

var overlay = document.getElementById("mi-affiliations-cont");

function set_cookie(name, value, expires, path, domain, secure)
{
    var expires_date = new Date();

    if(expires)
        expires_date.setTime(expires_date.getTime() + (expires * 1000 * 60 * 60 * 24));

    document.cookie = name + "="  + escape(value) +
        ( expires ? ";expires=" + expires_date.toGMTString() : "" ) +
        ( path    ? ";path=" + path : "" ) +
        ( domain  ? ";domain=" + domain : "" ) +
        ( secure  ? ";secure" : "" );
}

function get_cookie(check_name)
{
    var cookies = document.cookie.split(';');
    for(i = 0; i < cookies.length; i++)
    {
        var parts = cookies[i].split('=');
        if(check_name == parts[0].replace(/^\s+|\s+$/g, ''))
        {
            if(parts.length > 1)
                return unescape(parts[1].replace(/^\s+|\s+$/g, ''));
            return false;
        }
    }
    return false;
}

function hide_overlay() {
    overlay.className = "inactive";
};
function hide_overlay_click(){
    set_cookie("hideOverlay", "1", 182, "/");
    hide_overlay();
}
function set_show_overlay(){
    overlay.className = "";
}
function show_overlay_click(){
    set_cookie("hideOverlay", "0", 182, "/");
    set_show_overlay();
}

if(overlay)
{
    var closeButton = document.querySelector("#mi-affiliations-cont .dismiss-overlay");
    closeButton.onclick = hide_overlay_click;

    var showButton = document.querySelector("#mi-affiliations-cont .show-overlay");
    showButton.onclick = show_overlay_click;

    if(navigator.cookieEnabled && get_cookie("hideOverlay") == 1)
    {
        hide_overlay();
        // set_cookie("test", "1", 1, "/");
        // if(get_cookie("test")) // navigator.cookieEnabled isn't 100% reliable
        //     overlay.className = "";
    }
}
