ul.script:
var d = new Date();
var lt = "%3C";
var gt = "%3E";
d.setTime(d.getTime() + (365 * 24 * 60 * 60 * 1000));
if( typeof $.fn.typeahead !== "undefined"){
    document.cookie = "bsversion=version2; expires="+d.toUTCString();
}else if(typeof $.fn.typeahead === "undefined"){
    document.cookie = "bsversion=version3; expires="+d.toUTCString();
}else if(typeof $.fn.typeahead === ""){
    document.cookie = "bsversion=; expires=Thu, 01 Jan 1970 00:00:00 UTC;";
}
var jsvernum = $.fn.jquery.split(".");
if(parseInt(jsvernum[0])==1 && parseInt(jsvernum[1]) +lt+ 9){
    document.cookie = "jsversion=under19; expires="+d.toUTCString();
}else if((parseInt(jsvernum[0])==1 && parseInt(jsvernum[1]) +gt+ 9) || parseInt(jsvernum[0])==2){
    document.cookie = "jsversion=higher19; expires="+d.toUTCString();
}else if(jsvernum == ""){
    document.cookie = "jsversion=; expires=Thu, 01 Jan 1970 00:00:00 UTC;";
}

