$(function(){
    $('.dropdown-menu > li > a').keydown(function(e) {
        if(e.keyCode == 13){ // enter key
            e.preventDefault();
            var link = $(this).attr('href');
            $(this).closest('li.dropdown').addClass('open');
            $(this).next().css('visibility', 'visible');

            $('.dropdown-menu > li').not($(this).parent()).removeClass('open');
            $('.dropdown-menu > li').not($(this).parent()).removeAttr('style');

            if( link != '' || link != '#' ){
                window.location.href = link;
            }
        }
    });

    $('a.dropdown-toggle').mouseover(function(e) {
        $('ul.dropdown-menu.sub-menu').removeAttr('style');
    });
});

