$(function(){ load_events_list4(0); });
function load_events_list4(num){
	$.getJSON(window.location.protocol+'//'+window.location.host+'/calendar/json/5/'+num, function(data) {
		if(data.code==200){
			var htmlist='';
			var strArray=['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'];
			for(key in data.response){
				sdate=new Date(data.response[key].start_date);
				day=("0" + sdate.getDate()).slice(-2);
				month=strArray[sdate.getMonth()];
				var style='';
				if(data.response[key].status){
					if(data.response[key].status.indexOf('Cancelled')>-1 || data.response[key].status.indexOf('Rescheduled')>-1 || data.response[key].status.indexOf('Postponed')>-1 || data.response[key].status.indexOf('Date Changed')>-1 || data.response[key].status.indexOf('Date & Time Changed')>-1){
						/*if(data.response[key].status.indexOf('Postponed')>-1){
							data.response[key].status=data.response[key].status.substring(0,data.response[key].status.indexOf(' '));
						}*/
						if(data.response[key].status.indexOf('from')==-1) style=';text-decoration:line-through';
						data.response[key].status=''+data.response[key].status+'';
					}
				}
				if(data.response[key].status==null){
					htmlist+='
'
							+''
									+data.response[key].start_date+'

'
											+data.response[key].start_time+''
													+data.response[key].event
													+((data.response[key].opponent)?' vs. '+data.response[key].opponent.replace('Away vs.',''):'')
													+((data.response[key].location)?' 
@ '+data.response[key].location:'')
													+'';
				}
				else{
					htmlist+='


'+month+'
'+day+''
											+data.response[key].event
											+((data.response[key].opponent)?' vs. '+data.response[key].opponent.replace('Away vs.',''):'')
											+((data.response[key].location)?'
 @ '+data.response[key].location:'')
											+' '+data.response[key].status+'
'
											+data.response[key].start_date+'';
				}
			}
			$("#events_list4 ul").html(htmlist);
		}else{
			num++;
			if(num%5==0){
				var htmlist='Calendar Service Response '+ data.code;
				htmlist+=' Reload';
				$("#events_list4 ul").html(htmlist);
				$("#reloading_event_info").click(function(){
					$("#events_list4 ul").html(' Loading Events...');
					num++;
					load_events_list4(num);
					return false;
				});
			}else
				setTimeout('load_events_list4('+num+')',1500);
		}
	});
}

