div.script:
            $(window).load(function(){
            	var iframe = $('iframe.goog-te-menu-frame').contents();
                iframe.keydown(function(e){
                	if (e.keyCode == 40) { // down arrow get first link
                		e.preventDefault();
	                    e.stopPropagation();
	                    $(this).find('a:first').focus();
                	}
                });
                var menu = iframe.find('a').keydown(function(e){
                	var index = menu.index($(this));
                	if (e.keyCode == 40) { // down arrow
						e.preventDefault();
	                    e.stopPropagation();
						menu.eq(index + 1).focus();
					}
					if (e.keyCode == 38 && index > 0) { // up arrow
						e.preventDefault();
	                    e.stopPropagation();
						menu.eq(index - 1).focus();
					}
                });

            });

