body.script:
window.onload = init;
var topnavButton = document.getElementById('sitenav_main_mobile_toggle');
var topnavDisplay = document.getElementById('sitenav_main');

function init() {
	topnavButton.onclick = toggleNav;
}

function toggleNav(){
	topnavDisplay.classList.toggle("open");
	topnavButton.classList.toggle("open");
}

function videoTrigger(e) {
	e.preventDefault();
	var activeButton = $(e.currentTarget);

	if (activeButton.attr("class") == "video-nav") {

		activeButton.hasClass('active')?$(".video-item")[0].pause():$(".video-item")[0].play();
	} 

	activeButton.toggleClass('active');
}

$(".video-nav").on('click', videoTrigger);
