div.script:
Y.use('squarespace-ui-base', function(Y) {
  Y.one('h1.logo') && Y.one("h1.logo").plug(Y.Squarespace.TextShrink, {
    parentEl: Y.one('#logo .wrapper')
  });
  Y.one('.logo-subtitle') && Y.one(".logo-subtitle").plug(Y.Squarespace.TextShrink, {
    parentEl: Y.one('#logo .wrapper')
  });
});
