				'randomOrder'		: "off",
				'linkedElement'		: ['all'], //['picture', 'title', 'overlay', 'all']
				'thumbnails'        	: "on",
				'itemizedBullets'	: "off",
				'thumbnailWidth'	:  64,
				'thumbnailHeight'	:  51,
				'thumbnailControls'	: "on",
				'thumbnailLimit'	: 2, // to turn off thumbnail limits set to 0
				'animateType'		: "fade", //fade, slide
				'animateSpeed'		: 2,
				'pauseHover'		: [], //['picture', 'title', 'overlay', 'caption', 'all']
				'addClass'          	: "unclassed",
				'moreLinkText'      	: "off",
				'slideDirection'	: "horizontal", //horizontal, vertical
				'onImageLoad'		: function(){ },
				'onTransitionStart'	: function(){ },
				'onTransitionEnd'	: function(){ } 
			});
		}

		//DOC IF PHOTO GALLERY DOES NOT INITIALIZE THE REPLACE WITH IMAGE ONLY IF IN PREVIEW MODE
