				        	"descMoreLink"			: "no",
				        	"descMoreLinkText"		: "Read More",
				        	"bullets"			: "yes",
				        	"bulletControls"		: "no",
				        	"thumbnails"			: "no",
				        	"thumbnailWidth"		: 122,
				        	"thumbnailHeight"		: 53,
				        	"thumbnailSlideDirection"	: "horizontal", // horizontal or vertical
				        	"hoverPause"			: "yes",
