				        	}
					});
				} else {
					if($(".sw-special-mode-bar").length) {
						$("#hp-slideshow #sw-content-container10").append('




');
						$("").load(function() {
							$("#hp-slideshow .ui-widget.app.gallery.json.rotator-one .rs-photo-gallery-images-list li").append($(this));

							photoGalleryPlaceholderOne();
							$(window).bind("resize", function() { photoGalleryPlaceholderOne(); });
						});
					}
				}
			}
		}
	}

