link.script:
        $(document).ready(function(){
            var contextComm = parent.campusContextCommunicator;
            if(contextComm){
                contextComm.redirectToLogin();
            }

            $(window).load(function() {

                $("#lang2").change(function () {
                    var selectedLang = $('#lang2').val();
                    if(!selectedLang) {
                        selectedLang='en';
                    }

                    
                    changeLang(selectedLang); // Refresh page to update language selection
                    
                });
            });
        });

        var getUrlParameter = function getUrlParameter(sParam) {
            var sPageURL = decodeURIComponent(window.location.search.substring(1)),
                sURLVariables = sPageURL.split('&'),
                sParameterName,
                i;

            for (i = 0; i < sURLVariables.length; i++) {
                sParameterName = sURLVariables[i].split('=');

                if (sParameterName[0] === sParam) {
                    return sParameterName[1] === undefined ? true : sParameterName[1];
                }
            }
        };

div.script:
        var languageList = [];
        
        languageList.push('en');   //Here is the latest update check it sravan .Put single quotes.
        
        languageList.push('es');   //Here is the latest update check it sravan .Put single quotes.
        
        languageList.push('zh-Hans');   //Here is the latest update check it sravan .Put single quotes.
        
        languageList.push('zh-Hant');   //Here is the latest update check it sravan .Put single quotes.
        
        $(document).ready(function(){
            var language = getLanguage();
            $('#lang').val(language);
            $('#lang2').val(language);
        });
        function getLanguage() {
            paramLang = getUrlParameter('lang');

            if(paramLang) {
                var foundit = false;
                for(var v = 0; v < languageList.length; v++) {
                    if(languageList[v] === paramLang) {
                        foundit = true;
                        return paramLang;
                    }
                }
                if(!foundit) {
                    return 'en';
                }
            } else {
                var langCookie = getCookie('lang');
                // There was no language cookie set and nothing specified in the request/session, fall back to the default
                if(!langCookie) {
                    return "en";
                } else {
                    return langCookie;
                }
            }
        }
        function getCookie(cname) {
            var name = cname + "=";
            var ca = document.cookie.split(';');

            for(var i = 0; i <ca.length; i++) {
                var c = ca[i];
                while (c.charAt(0)==' ') {
                    c = c.substring(1);
                }

                if (c.indexOf(name) == 0) {
                    var foundit = false;
                    for(var v = 0; v < languageList.length; v++) {
                        if(languageList[v] === c.substring(name.length,c.length)) {
                            foundit=true;
                            return c.substring(name.length,c.length);
                        }
                    }
                    if(!foundit) {
                        return "";
                    }
                }
            }
            return "";
        }
