    // functions for later use ///////////////

    function convertISODate(ISOString) {
      var date = new Date(ISOString);
      return date;
    }


    // handles date object and returns a text string consisting of time of day in 12 hour, AM/PM format.
    function convertToTime(date) {
      var timeString;
      var afternoon = false;

      if (date.getHours() >= 12)
        afternoon = true;

      timeString = afternoon ? date.getHours() - 12 : date.getHours();
      timeString = timeString == 0 ? 12 : timeString;
      timeString += ":";
      timeString += ( date.getMinutes() % 100 == 0 ) ? "0" + date.getMinutes() : date.getMinutes();
      timeString += " ";
      timeString += afternoon ? "PM" : "AM";
      if(timeString == "12:00 AM")timeString = "All Day";
      return timeString;
    }
    function formatMonth(month){
        var form_month = '';
        if(month == 1) form_month="Jan";
        if(month == 2) form_month="Feb";
        if(month == 3) form_month="Mar";
        if(month == 4) form_month="Apr";
        if(month == 5) form_month="May";
        if(month == 6) form_month="Jun";
        if(month == 7) form_month="Jul";
        if(month == 8) form_month="Aug";
        if(month == 9) form_month="Sept";
        if(month == 10) form_month="Oct";
        if(month == 11) form_month="Nov";
        if(month == 12) form_month="Dec";
        return form_month;
    }


    // encapsulate generation of single event element, return DOM object.
    function generateElement(isToday, calEvent) {
        // Construct container for each event.
        // eventCont > linkify > info_cont > info_time + info_location
      var startTime = (calEvent.start.dateTime)?convertISODate(calEvent.start.dateTime):convertISODate(calEvent.start.date+"T00:00:00-06:00");
      var dateString = convertToTime(startTime);
      //if (!isToday) dateString += " on " + generateDate(startTime);

      var eventElement = document.createElement('li');
      $(eventElement).addClass('eventElem');

      var info_date = document.createElement('div');
      $(info_date).addClass("datestamp")
                    .appendTo($(eventElement));

      var info_month = document.createElement('div');
      $(info_month).addClass("month")
                    .text(formatMonth(startTime.getMonth() + 1))
                    .appendTo($(info_date));

      var info_day = document.createElement('div');
      $(info_day).addClass("date")
                    .text(startTime.getDate())
                    .appendTo($(info_date));

      var info_title = document.createElement('a');
      $(info_title).addClass("title")




                    .attr("href",calEvent.htmlLink+"&ctz="+tz)
                    .attr("onclick","window.open('"+ calEvent.htmlLink +"&ctz="+tz+"','','width=450,height=450,left='+(screen.availWidth/2-225)+',top='+(screen.availHeight/2-225)+'');return false;")
                    .attr("target","_blank")
                    .text(calEvent.summary)
                    .appendTo($(eventElement));

      var info_cont = document.createElement('div');
      var date_location = (calEvent.location)?dateString + ' • ' +calEvent.location:dateString;
      $(info_cont).addClass('details')
                  .html(date_location)
                  .appendTo(eventElement);

      return eventElement;
    }


    // populate calendar. 
    function sortAndFill(eventList) {
      var today = new Date();
      var i; //index
      for (i = 0; i < eventList.length; i++) {
        if (convertISODate(eventList[i].start.dateTime) > today.getDate()) break;
      }

      for (i; i < eventList.length; i++)
        $('#districtEvents').append($(generateElement(false, eventList[i])));

      if ($('#districtEvents').children().length === 1)
        $('#districtEvents').append("
No events scheduled!");

      // fill.call(this, false, fill.call(this, true, 0));
    }
