			var _ElementDefinition = /**
 * This is required for element rendering to be possible
 * @type {PlatformElement}
 */
( function() {

    var IconBox,
        resizeTimeout,
        that;

    that = this;
    resizeTimeout = null;

    IconBox = PlatformElement.extend( {

        "setIcon": function() {

            var $icon,
                iconName,
                transform;

            //  get settings
            iconName = that.settings.get( "name" );
            iconName = iconName.replace( /^fa-/, "" );
            transform = that.settings.get( "transform" );

            //  find icon and update its classes
            $icon = that.$el.find( ".icon-box-content i.fa" );
            $icon.removeClass().addClass( "fa fa-" + iconName + " " + transform );

            //  done
            return $icon;
        },

        "addLink": function( $icon ) {

            var $a,
                newWindow,
                url;

            //  get settings
            url = that.settings.get( "url" );
            newWindow = String( that.settings.get( "newWindow" ) );
            newWindow = ( newWindow === "1" || newWindow === "true" );

            //  wrap a link around the icon
            if ( url ) {
                $a = $( "" ).attr( "href", url );
                if ( newWindow ) {
                    $a.attr( "target", "_blank" );
                }
                $icon.wrap( $a );
            }
        },

        "installResizeHandler": function() {

            $( window ).resize( function() {

                // ignore resize events as long as an actualResizeHandler execution is in the queue
                if ( resizeTimeout === null ) {
                    resizeTimeout = setTimeout( function() {
                        resizeTimeout = null;
                        that.actualResizeHandler();
                    }, 66 );                            //  66ms: the actualResizeHandler will execute at a rate of 15fps
                }
            } );            
        },

        "actualResizeHandler": function() {
            that.updateWidth();
        },

        "updateWidth": function() {

            var $box,
                $icon,
                size,
                width;

            setTimeout( function() {

                $box = that.$el.find( ".icon-box-content" );
                $icon = that.$el.find( ".icon-box-content i.fa" );
                size = that.settings.get( "size" );

                if ( $box.length === 0 ) {
                    console.log( "ICON: Box not found!" );
                    return;
                }

                if ( $icon.length === 0 ) {
                    console.log( "ICON: Icon not found!" );
                    return;
                }

                if ( !size ) {
                    console.log( "ICON: Size not found!" );
                    return;
                }

                width = $box.width();
                $icon.css( {
                    "font-size": width * Number( size ) / 100,
                    "transition": "font-size 100ms ease-in"
                } );
            }, 500 );

        },

        "initialize": function() {

            var $icon;

            that = this;

            //  set the icon
            $icon = that.setIcon();

            //  add link, if necessary
            that.addLink( $icon );

            //  update the width of the element based on the container size
            that.updateWidth();

            //  need to resize the element when the window resizes
            that.installResizeHandler();
        }

    } );

    return IconBox;
}() );
