div.script:
$(".group844134").colorbox({rel:'group844134', maxWidth: '80%'});
    $( window ).resize( function(){
        if( $('#cboxOverlay' ).css( 'visibility' ) ){
        $(".group844134").colorbox.resize({ innerWidth: '80%', scalePhotos: 'true'});
    }
});
var $gallery844134 = $("a[rel=group844134]").colorbox({maxWidth: "80%"});
$("a#openGallery844134").click(function(e){
    e.preventDefault();
    $gallery844134.eq(0).click();
});

