	function jsMediaQueries() {
		switch(csGetBreakPoint()) {
			case "desktop": 
				tabAccordionReset();
                globalIconsReset();
			break;
            case "1024":
            	globalIconReset();
			case "768":
				tabAccordionReset();
                moveGlobalIconsToMyStart();
			break;
			case "640":
				tabAccordionReset();
                moveGlobalIconsToMyStart();
			break;
			case "480":
				tabAccordion();               
                globalIconsReset();
			break;
			case "320":
				tabAccordion();
			break;
		}
	}
	function moveGlobalIconsToMyStart() {
    	$(".global-icon").each(function() {
        $('#right-header-container').css('display, none');
          $(this).appendTo("#sw-mystart-left");
        });
    }
    function globalIconsReset() {
    	$(".global-icon").each(function() {
        	$('#right-header-container').css('display, block');
          $(this).appendTo("#gb-global-icons");
        });
    }

	function photoGallery(){
		head.ready("photoGallery", function(){
			if(!$("#sw-content-container15.region.ui-hp .rs-photo-gallery-container").length){
				// PHOTO GALLERY ROTATOR
				$("#sw-content-container15.ui-hp div.ui-widget.app.gallery.json").creativeGalleryRotate({
                    "imageWidth"                : 1220,
                    "imageHeight"               : 510,
                    "efficientLoad"             : "yes",
                    "appHeader"                 : "no",
                    "titles"                    : "yes",
                    "captions"                  : "yes",
                    "playPause"                 : "no",
                    "mainControls"              : "yes",
                    "galleryOverlay"            : "no",
                    "linkedElement"             : ["image"], // ["image", "title", "overlay", "all"]
                    "contMoreLink"              : "yes",
                    "contMoreLinkText"          : "Read More",
                    "descMoreLink"              : "no",
                    "descMoreLinkText"          : "Description Read More",
                    "bullets"                   : "no",
                    "bulletControls"            : "no",
                    "thumbnails"                : "no",
                    "thumbnailWidth"            : 122,
                    "thumbnailHeight"           : 53,
                    "thumbnailSlideDirection"   : "horizontal", // horizontal or vertical
                    "hoverPause"                : "yes",
                    "fullScreenRotator"         : "no", // image dimensions work well at 1500x915
                    "fullScreenBreakpoints"     : ["960", "768", "640", "480", "320"], // OPTIONS ARE ["960", "768", "640", "480", "320"]
                    "autoRotate"                : "yes",
                    "transitionType"            : "fade", // fade or slide
                    "transitionSpeed"           : 2,
                    "onImageLoad"               : function() { },
                    "allImagesLoaded"           : function() { },
                    "onThumbnailLoad"           : function() { },
                    "allThumbnailsLoaded"       : function() { },
                    "onTransitionStart"         : function() { },
                    "onTransitionEnd"           : function() { },
                    "allLoaded"                 : function() { 
                    	$('.rs-photo-gallery-more-link').appendTo('.rs-photo-gallery-desc');
                        $('#hp-slideshow .rs-photo-gallery-control.back, #hp-slideshow .rs-photo-gallery-control.next').clone(true).addClass('mobile-slideshow-arrows').appendTo('#hp-slideshow .rs-photo-gallery-desc');
                    }
				});
			}
		});
	}
     // GLOBAL ICONS
    function globalIcons() {
      head.ready("globalIcons", function() {
          $("#gb-global-icons").creativeIcons({
                    "iconNum"       : "3", // NUMBER OF ICONS
                    "icons"         : [
                            ["/cms/lib/NJ01001806/Centricity/Template/GlobalAssets/icons/CustomGlobal-Aplus.png", "Grades", "http://www.bhstudentinfo.org", "_blank"], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
                            ["Communication/18.png", "Email", "https://www.bhpsnj.org/email", "_blank"], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
                            ["/cms/lib/NJ01001806/Centricity/Template/GlobalAssets/icons/CustomGlobal-Apple.png", "Staff Directory", "https://www.bhpsnj.org/staff-directory", "_blank"]  // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
                    ],
                    "siteID"        : "4",
                    "siteAlias"     : "bhpsnj",
                    "calendarLink"  : "/Page/2",
                    "contactEmail"  : "mskara@bhpsnj.org",
                    "allLoaded"     : function(){ 
