	function dynamicContent() {
		// ********** MYSTART ********** //
		$("#sw-search-input").unbind();
		$(".sw-mystart-dropdown").click(function(e) {
			$(".sw-dropdown", this).slideDown(300, "swing");
			$(this).addClass("open");
		});
		$(".sw-mystart-dropdown").hover(function(e) {
			e.preventDefault();
		}, function() {
			$(".sw-dropdown",this).slideUp(300, "swing");
			$(this).removeClass("open");
		});
        $('.sw-mystart-button.home span').text('DISTRICT');
        $('.sw-mystart-dropdown.schoollist .selector').text('OUR SCHOOLS');
        $('.sw-mystart-dropdown.translate').insertAfter('#sw-mystart-account');
        $('#sw-mystart-right > .sw-mystart-button.manage').remove();
        $('#sw-myaccount').text('USER OPTIONS');
        $('Search: ').insertBefore('#sw-search-input');
        $("div#sw-mystart-search.sw-mystart-nav").unbind();

		// ********** HEADER ********** //
		// ADD LOGO
		var logoCheck = "true";
		var logoSrc = jQuery.trim("/cms/lib/NJ01001806/Centricity/Template/GlobalAssets/images//logos/BPHS-District-4web-IIE2.png");
		if(logoCheck == "true") {
			var srcSplit = logoSrc.split("/");
			var srcSplitLen = srcSplit.length;
			if((logoSrc != "") && (srcSplit[srcSplitLen - 1] != "default-man.jpg")) {
				$("#gb-logo").append('').removeClass("hidden");
			} else {
				$("#gb-logo").append('').removeClass("hidden");
			}
		}

		// ADD SITENAME
		var siteNameOne = jQuery.trim('');
		var siteNameTwo = jQuery.trim('');
		if((siteNameOne == "") && (siteNameTwo == "")) {
			var splitLen = 2;
			var siteName = "Berkeley Heights Public Schools";
			siteName = siteName.split(" ");
			var siteNameLength = siteName.length;
			if(siteNameLength > 2){
				siteNameEnd = jQuery.trim(siteName.splice(-splitLen, siteName.length).toString().replace(/,/g, " "));
			} else {
				siteNameEnd = jQuery.trim(siteName.splice(-1, siteName.length).toString().replace(/,/g, " "));
			}
			siteNameBegin = jQuery.trim(siteName.toString().replace(/,/g, " "));
			$("#gb-sitename").prepend("
" + siteNameBegin + "
" + siteNameEnd + "");
		} else if((siteNameOne != "") && (siteNameTwo == "")) {
			$("#gb-sitename").prepend("
" + siteNameOne + "");
		} else if((siteNameOne == "") && (siteNameTwo != "")) {
			$("#gb-sitename").prepend("
" + siteNameTwo + "");
		} else if((siteNameOne != "") && (siteNameTwo != "")) {
			$("#gb-sitename").prepend("
" + siteNameOne + "
" + siteNameTwo + "");
		}

		// ********** CHANNEL BAR ********** //
		$(".sw-channel-item").unbind('hover');
		$(".sw-channel-item").hover(function(){
			$(".sw-channel-item ul").stop(true, true);
			var subList = $(this).children('ul');
			if ($.trim(subList.html()) !== "") {
				subList.slideDown(300, "swing");
			}
			$(this).addClass("hover");
		}, function(){
			$(".sw-channel-dropdown").slideUp(300, "swing");
			$(this).removeClass("hover");
		});

		// ********** BODY ********** //
		// AUTO FOCUS SIGN IN FIELD
		$("#swsignin-txt-username").focus();

		// CENTER SIGNED OUT MESSAGE AND SIGNIN BUTTON
        if($("div.ui-spn > div > p > span").text() == "You have been signed out."){
            $("div.ui-spn > div").css({"text-align" : "center", "padding" : "50px 0px 50px 0px"});
            //DOC add signed out breadcrumb
            $(".ui-breadcrumbs").append("
Signed Out");
        }

        // ADJUST SIGN IN PAGE
        if($("#swlogin").length){
            $("#sw-content-layout-wrapper .ui-widget.app").addClass("signin");
            $(".ui-breadcrumbs").append("
Sign In");
        }

        // ADJUST REGISTER PAGE
		if($("#swageprompt-txt-birthmonth").length && $("#swageprompt-txt-birthday").length && $("#swageprompt-txt-birthyear").length) {
			$("#sw-content-layout-wrapper .ui-widget.app").addClass("register");
			$(".ui-breadcrumbs").append("
Register");
		}

		// ADJUST SEARCH RESULTS PAGE
		if($("#sw-content-layout-wrapper.ui-spn #swsearch-pnl-main").length) {
			$("#sw-content-layout-wrapper").children().wrapAll('

');
			$(".ui-breadcrumbs").append("
Search Results");
		}

        // ADD READ FULL STORY BUTTON TO EACH HEADLINE
        $(".ui-widget.app.headlines").each(function(){
            $(".ui-article", this).each(function(){
                var headlineURL = $("h1 a", this).attr("href");
                $(".ui-article-description", this).append('');
            });
        });

        // APPLY RESPONSIVE DIMENSIONS TO CONTENT IMAGES
        $("div.ui-widget.app .ui-widget-detail img")
            .not($("div.ui-widget.app.cs-rs-multimedia-rotator .ui-widget-detail img"))
            .not($("div.ui-widget.app.gallery.json .ui-widget-detail img"))
            .each(function() {
                if ($(this).attr('width') !== undefined && $(this).attr('height') !== undefined) { // IMAGE HAS INLINE DIMENSIONS
                    $(this).css({"display": "inline-block", "width": "100%", "max-width": $(this).attr("width") + "px", "height": "auto", "max-height": $(this).attr("height") + "px"});
                }
        });

        // MAKE NAVIGATION APPS ACCESSIBLE
        head.ready("accessibleNavApp", function(){
            $(".ui-widget.app.navigation").csAccessibleNavigationApp();
        });

        // MOVES THESE ELEMENTS OUT OF A LIST - DONE FOR ACCESSIBILITY PURPOSES
        $('.headlines a.more-link').attr('aria-label', 'View all headlines');
        $('.headlines .ui-article-thumb').attr('aria-label', 'headline thumbnail image');
        $('#sw-search-button').attr('role', 'button');
        $('#sw-search-button').attr('aria-label', 'Submit Search');

		// ********** FOOTER ********** //
		// MOVE SCHOOLWIRES FOOTER STUFF
		$(".gb-schoolwires-footer.logo").html($("#sw-footer-logo").html());
		$(".gb-schoolwires-footer.links").append($("#sw-footer-links li:eq(0) a"));
		$(".gb-schoolwires-footer.links").append($("#sw-footer-links li:eq(2) a"));
		$(".gb-schoolwires-footer.links").append($("#sw-footer-links li:eq(1) a"));
		$(".gb-schoolwires-footer.links").append($("#sw-footer-copyright"));		
	}

