	function searchText() {
    	var csKeyCodes = { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 };
        $("#sw-mystart-search").on("click keydown", "#sw-search-button", function(e){
        	if(e.type == "click" || e.type == "keydown" && e.keyCode == csKeyCodes.space || e.type == "keydown" && e.keyCode == csKeyCodes.enter) {
            	e.preventDefault();
            	SWGoToSearchResultsPageswsearchinput();
            }
        });
        //SEARCH TEXT
        $("div#sw-mystart-search input#sw-search-input").removeAttr("placeholder");
        $("div#sw-mystart-search input#sw-search-input").attr("value", "SEARCH...");

        $("div#sw-mystart-search input#sw-search-input").focus(function() {
        	if($(this).val() == "SEARCH...") {
        		$(this).val("");
        	}
        });

        $("div#sw-mystart-search input#sw-search-input").blur(function() {
        	if($(this).val() == "") {
        		$(this).val("SEARCH...");
        	}
        });
	}

    function modEvents(){
        head.ready("modEvents", function() {
			$(".upcomingevents").each(function() {
				if (!$(".upcomingevents .joel-month", this).length) {
					$(this).modEvents({
                      columns     : "yes",
                      monthLong   : "no",
                      dayWeek     : "yes"
            		});
            eventsByDay(".upcomingevents .ui-articles");
        		}
    		});
    	});
	}


    function eventsByDay(container) {

    	// LOOP THROUGH EACH EVENT
        $('.upcomingevents .ui-widget-detail .ui-articles > li').each(function (k,v) {
        	if ($(".joel-month", v).length <= 0) {
            	var prevEvent = $(v).prev("li");
                $(".joel-day", prevEvent).clone().prependTo($(".ui-article", v));
                $(".upcoming-column.left", prevEvent).clone().prependTo($(".ui-article", v));
                $(".ui-article", v).addClass("ui-clear upcoming-column-container");
                $(".ui-article-description", v).wrap('
');
            }
            $(".upcoming-column.left", v).after($(".joel-day", v));
            if ($(".sw-calendar-block-time", v).length <= 0) {
            	$(".ui-article-description", v).prepend('ALL DAY');
            }
        });
    }


	function calendarListView() {
		// AUTO CLICK FUNCTION FOR SCHOOLWIRES CALENDAR LIST VIEW FOR SMALL MOBILE DEVICES
		if(!$(".ui-widget.app.calendar #calendarlist-pnl-specialview.loaded").length){
			$("a.ui-btn-toggle.list").click();
			$("a.ui-btn-toggle.month").click();
			$(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("loaded");
		}

		switch(csGetBreakPoint()) {
			case "desktop": case "768": case "640":
				$(".ui-widget.app.calendar #calendar").removeClass("ui-helper-hidden");
				$(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("ui-helper-hidden");
			break;
			case "480": case "320":
				$(".ui-widget.app.calendar #calendar").addClass("ui-helper-hidden");
				$(".ui-widget.app.calendar #calendarlist-pnl-specialview").removeClass("ui-helper-hidden");
				$(".ui-btn-toggle.month").removeClass("active");
				$(".ui-btn-toggle.list").addClass("active");
			break;
		}
	}

	function csGetBreakPoint() {
		return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
	}

    function tabbedApps() {
		head.ready("tabbedApps", function() {
			$("#sw-content-container2.ui-hp").tabBlocks();
			$(".tabs-container .clear").remove();
			$(".tabs-container .tab-button").each(function() {
				$(".tab-left", this).remove();
			});
			$("#sw-content-container2.ui-hp").children().not($(".tabs-container")).wrapAll('
');
		});
	}

    function tabAccordion(){
		if(!tabAccordionSet) {
			// MAKE HOMEPAGE APPS COLLAPSIBLE FOR SMALL MOBILE DEVICES
			if(!$("#sw-content-container2.ui-hp div.ui-widget.app.active").length){
				$("#sw-content-container2.ui-hp div.ui-widget.app:first").addClass("active");
			}

			// ACCORDION HEADER CLICK
			$("#sw-content-container2.ui-hp div.ui-widget-header").click(function(){
				if($(this).parent().hasClass("active")) {
					$(this).parent().removeClass("active");
				} else {
					$("#sw-content-container2.ui-hp div.ui-widget.app.active").removeClass("active");
					$(this).parent().addClass("active");

					$("html, body").animate({
						scrollTop: $(this).offset().top
					}, 500, function(){
						$("html, body").stop(true, false);
					});
				}
			});

			tabAccordionSet = true;
		}
	}

	function tabAccordionReset() {
		if(tabAccordionSet) {
			$("#sw-content-container2.ui-hp div.ui-widget-header").unbind("click");

			tabAccordionSet = false;
		}
	}

