        "MyStart": function() {
        	if($(".sw-mystart-button.signin").length > 0){ //SignIn
                $('#custom-mystart-signin').append($(".sw-mystart-button.signin").html());
                $('#custom-mystart-signin a').addClass("mystart-label").prepend('');

                if($(".sw-mystart-button.register").length > 0){ //Register
                    $('#custom-mystart-register').append($(".sw-mystart-button.register").html());
                    $('#custom-mystart-register a').addClass("mystart-label").prepend('|');
                }
            }
            if($('.sw-mystart-button.manage').length > 0){ //Site Manager
                $('#gb-user-options ul').append("
" + $('.sw-mystart-button.manage').html() + "");
                $('#sw-mystart-right > .sw-mystart-nav.sw-mystart-button.manage > a').remove();
            }
            if($('#sw-mystart-mypasskey').length > 0){ //Passkey
                $('#sw-mystart-mypasskey').removeClass('sw-mystart-button').appendTo("#cs-mystart-left");
                $('#ui-btn-mypasskey').addClass('mystart-label');
                $('#ui-btn-mypasskey span').html('Passkeys').before('');
            }
            if( $('#ui-btn-myview').length > 0 ){ //MyView
            	$('#gb-user-options ul').append("
" + $('.sw-mystart-button.pw').html() + "");
                $('.sw-mystart-button.pw').remove();
            }

            $('#sw-myaccount-list li').appendTo('#gb-user-options ul');

            $('.sw-mystart-dropdown.schoollist ul.sw-dropdown-list li').clone().appendTo('#school-list');

            this.Translator();

            $('#cs-find-it-fast-outer .max-width').click(function(e){
            	e.stopPropagation();
            });

            $('#cs-find-it-fast-outer, #cs-find-it-fast-close-trigger').click(function(e){
            	if($('#cs-find-it-fast-outer').attr('aria-hidden') == 'false'){
                	$('#cs-find-it-fast-outer').fadeOut(500, function(e){
                        $('#cs-find-it-fast-outer').attr('aria-hidden', 'true');
                    });

                    $('#cs-find-it-fast-trigger').focus();
                }
            });

            $('#cs-find-it-fast-close-trigger').keydown(function(e){
            	if(e.keyCode == 9 && e.shiftKey){
                	e.preventDefault();
                	$('#cs-find-it-fast-outer a, #cs-find-it-fast-outer button').last().focus();
                }
            });

            $('#cs-find-it-fast-trigger').click(function(e){
            	e.stopPropagation();
            	if($('#cs-find-it-fast-outer').attr('aria-hidden') == 'true'){
                	$('#cs-find-it-fast-outer')
                    	.attr('aria-hidden', 'false')
                        .stop(true)
                        .hide(0)
                        .fadeIn(500);

                    setTimeout(function(e){
                    	$('#cs-search-container input[type="text"]').focus();
                    }, 200);
                }
            });
        },
        "Header": function() {
            // ADD LOGO
