        "Search": function() {
        	var defSearchText = "SEARCH";

            var searchBox = $('#swsitesearch-txt-searchstring');

            searchBox
                .attr("placeholder", '')
                .val(defSearchText)
                .focus(function() {
                    if($(this).val() == defSearchText) {
                        $(this).val("");
                    }
                })
                .blur(function() {
                    if($(this).val() == "") {
                        $(this).val(defSearchText);
                    }
                });

			$('#cs-submit-search')
            	.attr('aria-label', $('#search-container input[type="button"]').attr('aria-label'))
                .click(function(e){
                    $('#cs-search-container input[type="button"]').click();
                });
        },
        "SmoothScroll": function(to, offset){
        	/*
            ** smoothScroll
            ** Use: This will smoothly scroll to the id that was passed to the function.
            ** Return: N/A
            ** Parameter(s): to (jQuery "#id") The id of the section to be scolled to.
            ** 		offset (int) The amount of space you want to be left on the top of the element after scroll (in a whole number int value)
            */
        	if( to.indexOf("#") < 0 ){ to = "#" + to; } //makes sure we have a valid id accessor string
            var target = $(to);

            if (target.length) {
                $('html,body').animate({
                    scrollTop: target.offset().top - offset //this offset accounts for the fixed menu on the top
                }, 800, function(){
                    $("html, body").stop(true, false);
                });
                return false;
            }
        },
        "ContentDisplayer": function(){
        	if($('.hp-row').length > 0 && isNaN(CreativeTemplate.GetBreakPoint())){
                setTimeout(function(){
                    $('.hp-content-animate.content-hidden').each(function(rowIndex, row){
                        var rowRect = document.getElementById(row.getAttribute('id')).getBoundingClientRect();

                        if(rowRect.top <= $(window).height()*0.65 && rowRect.top >= -400){ //catchs the elements as they pass right below the menu bar
                            //show the apps
                            CreativeTemplate.ShowApps(row);
                            $(row).removeClass('content-hidden');
                            return false;
                        }
                    });

                    $(window).scroll(function(e){
                        $('.hp-content-animate.content-hidden').each(function(rowIndex, row){
                            var rowRect = document.getElementById(row.getAttribute('id')).getBoundingClientRect();

                            if(rowRect.top <= $(window).height()*0.80 && rowRect.top >= -400){ //catchs the elements as they pass right below the menu bar
                                //show the apps
                                CreativeTemplate.ShowApps(row);
                                $(row).removeClass('content-hidden');
                            }
                        });
                    });
            	}, 500);
            }else if(CreativeTemplate.GetBreakPoint() < 1024){
            	$('.hp-content-animate.content-hidden').each(function(rowIndex, row){
                    CreativeTemplate.ShowApps(row);
                    $(row).removeClass('content-hidden');
                });
            }
        },
        "ShowApps": function(row){

            var animationProperties = {'opacity': 1};
            var animationSettings = {
                'duration': 900
			};

            switch($(row).attr('animation-type')){
            	case "slide-in-left":
                	$('.ui-widget.app .ui-widget-detail', row).css('left', '-90px');
                    animationProperties['left'] = 0;
                    animationSettings['easing'] = 'easeOutBounce';
                	break;
                case "slide-in-right":
                	$('.ui-widget.app .ui-widget-detail', row).css('right', '-90px');
                    animationProperties['right'] = 0;
                    animationSettings['easing'] = 'easeOutBounce';
                	break;
                default: break;
            }

        	$('.ui-widget.app', row).each(function(appIndex, app){
            	animationSettings['always'] = function(e){
                    //if there is a header accent shadow fade it in now for flare!
                    $('.ui-widget-header.has-shadow-script .header-accent-text', app).animate({'opacity': 1}, 400);
                };

                $('.ui-widget-detail', app).animate(animationProperties, animationSettings);

                setTimeout(function(){}, 500);
            });
        },
        "HpEvents": function() {
            var template = this;

            $("#hp-featured-ue .ui-widget.app.upcomingevents").each(function(i) {
                if($(this).closest("div[id*='pmi-']").next("div[id*='pmi-']").length) {
                    $(this).addClass("pad-bottom");
                }

                var thisApp = this;
                if($('.ui-widget-detail .cs-featured-ue-controls', this).length <= 0){
                	$('.ui-widget-detail', this).append('
Expand Events');				
                }

                $(".ui-articles", this)
                	.attr('id', 'cs-featured-ue-detail-' + i)
                	.removeAttr("style");
                $(".ui-articles > li", this).removeAttr("aria-hidden");
                $(".ui-articles > li a", this).removeAttr("tabindex");
                $(".ui-articles > li .upcoming-column.right", this).removeAttr("aria-hidden");

                var eventsPerRow = 0;
                var eventsProps = {
                    "eventNum": $(".ui-articles > li", this).length,
                    "eventTotalHeight": $(".ui-articles", this).outerHeight(true),
                    "eventPanelHeight": $(".ui-articles > li:first-child", this).outerHeight(true) + $(".ui-articles > li:first-child .upcoming-column.left .sw-calendar-block-date", this).outerHeight(true), // HEIGHT OF FIRST ARTICLE + HEIGHT OF MONTH/DAY BLOCK
                    "desktop": {
                        "eventsPerRow": 4,
                        "numOfRowsToShow": 2
                    },
                    "sevenSixtyEight": {
                        "eventsPerRow": 2,
                        "numOfRowsToShow": 2
                    },
                    "sixForty": {
                        "eventsPerRow": 2,
                        "numOfRowsToShow": 2
                    }
                }

                switch(template.GetBreakPoint()) {
                    case "desktop":
                        eventsPerRow = eventsProps.desktop.eventsPerRow;
                    break;
                    case "768":
                        eventsPerRow = eventsProps.sevenSixtyEight.eventsPerRow;
                    break;
                    case "640":
                        eventsPerRow = eventsProps.sixForty.eventsPerRow;
                    break;
                }

                if(eventsProps.eventNum > eventsPerRow) {
                    $(".cs-events-toggle", this).attr("aria-label", "Expand Events").text("Expand Events").addClass("collapsed").removeClass("expanded");

                    $(".ui-articles > li:nth-child(n+" + ((eventsPerRow * 2) + 1) + ")", this).attr("aria-hidden", "true");
                    $(".ui-articles > li:nth-child(n+" + (eventsPerRow + 1) + ") a", this).attr("tabindex", "-1");
                    $(".ui-articles > li:nth-child(n+" + (eventsPerRow + 1) + "):nth-child(-n" + (eventsPerRow * 2) + ") .upcoming-column.right", this).attr("aria-hidden", "true");

                    $(this).addClass("expandable");
                    $(".ui-articles", this).height(eventsProps.eventPanelHeight);
                } else {
                    $(".ui-articles > li", this).removeAttr("aria-hidden");
                    $(".ui-articles > li a", this).removeAttr("tabindex");
                    $(".ui-articles > li .upcoming-column.right", this).removeAttr("aria-hidden");

                    $(this).removeClass("expandable");
                    $(".ui-articles", this).css("height", "auto");
                }

                $('#hp-featured-ue .cs-featured-ue-trigger').off('click').click(function(e) {
                    if(template.AllyClick(e)) {
                        e.preventDefault();

                        // EXPAND EVENTS
                        if($(this).hasClass("collapsed")) {
                            $(this).attr("aria-label", "Collapse Events").html("Collapse Events").addClass("expanded").removeClass("collapsed");

                            $(".ui-articles > li", thisApp).removeAttr("aria-hidden");
                            $(".ui-articles > li a", thisApp).removeAttr("tabindex");
                            $(".ui-articles > li .upcoming-column.right[aria-hidden='true']:first", thisApp).find("a:first").focus();
                            $(".ui-articles > li .upcoming-column.right", thisApp).removeAttr("aria-hidden");

                            $(".ui-articles", thisApp).stop(true).animate({
                                height: eventsProps.eventTotalHeight
                            }, 500);
                        }

                        // COLLAPSE EVENTS
                        else {
                            $(this).attr("aria-label", "Expand Events").html("Expand Events").addClass("collapsed").removeClass("expanded");

                            $(".ui-articles > li:nth-child(n+" + ((eventsPerRow * 2) + 1) + ")", thisApp).attr("aria-hidden", "true");
                            $(".ui-articles > li:nth-child(n+" + (eventsPerRow + 1) + ") a", thisApp).attr("tabindex", "-1");
                            $(".ui-articles > li:nth-child(n+" + (eventsPerRow + 1) + "):nth-child(-n" + (eventsPerRow * 2) + ") .upcoming-column.right", thisApp).attr("aria-hidden", "true");

                            $(".ui-articles", thisApp).animate({
                                height: eventsProps.eventPanelHeight
                            }, 500);
                        }
                    }
                });
            });
        },


