        "SchoolList": function() {
            // FOR SCOPE
            var template = this;

            // ADD SCHOOL LIST
            $.schoolListMinibase({
                'miniBaseDomainID': 4,
                'miniBaseModuleInstanceID': 16967,
                'cache': true,
                'schoolListReady': function(schoolList) {
                    /*
                    The returned value is an array of objects and each object is a school record.
                    The fields are: schoolList.schoolName, schoolList.schoolType, schoolList.schoolUrl, schoolList.schoolAlias, schoolList.schoolVisible
                    */

                    var schools = '', sortedSchools = {};

                    schoolList.sort();     

                    // SORT THE SCHOOL LIST TO TRY TO GET THE EXPECTED TYPES IN THE ORDER WE WANT
                    var expectedNameOrder = ["district", "elementary", "middle", "high"];
                    schoolList.sort(function(a, b) {
                        // WE WANT THE SPECIFIC CATEGORIES ABOVE TO COME FIRST AND IN THE SPECIFIED ORDER
                        // WE'LL CAPTURE THOSE IN THIS FIRST CHECK
                        if(expectedNameOrder.indexOf(a.schoolType.toLowerCase()) > -1 && expectedNameOrder.indexOf(b.schoolType.toLowerCase()) > -1) {
                            if(expectedNameOrder.indexOf(a.schoolType.toLowerCase()) < expectedNameOrder.indexOf(b.schoolType.toLowerCase())) {
                                return -1;
                            }
                            if(expectedNameOrder.indexOf(a.schoolType.toLowerCase()) > expectedNameOrder.indexOf(b.schoolType.toLowerCase())) {
                                return 1;
                            }
                        }

                        // HERE, THE SCHOOL CATEGORY DOESN'T EXIST IN THE CATEGORY ARRAY ABOVE
                        else {
                            if(expectedNameOrder.indexOf(a.schoolType.toLowerCase()) > expectedNameOrder.indexOf(b.schoolType.toLowerCase())) {
                                return -1;
                            }
                            if(expectedNameOrder.indexOf(a.schoolType.toLowerCase()) < expectedNameOrder.indexOf(b.schoolType.toLowerCase())) {
                                return 1;
                            }
                        }

                        // NEITHER CHECK APPLIES SO WE JUST LEAVE THIS IN IT'S CURRENT POSITION
                        return 0;
                    });

                    // SORT THE SCHOOLS INTO TYPE OBJECTS
                    for(var type in schoolList) {
                        if(schoolList[type].schoolVisible.toLowerCase() == 'true') {
                            var thisType = schoolList[type].schoolType.toLowerCase();

                            // CHECK FOR MULTIPLE COMMA SEPARATED VALUES
                            if(schoolList[type].schoolType.indexOf(",") > -1) {
                                var subTypes = schoolList[type].schoolType.split(",");

                                // LOOP THROUGH THE SUB TYPES
                                for(var subType in subTypes) {
                                    var thisSubType = $.trim(subTypes[subType]).toLowerCase();

                                    // ADD THIS TYPE OBJECT TO THE SORTED SCHOOLS OBJECT IF IT'S NOT THERE YET
                                    if(!sortedSchools.hasOwnProperty(thisSubType)) {
                                        // ADD THIS TYPE VALUE TO THE SORTED SCHOOLS OBJECT AND ADD THIS SCHOOL RECORD
                                        sortedSchools[thisSubType] = [schoolList[type]];
                                    } else {
                                        // PUSH THIS SCHOOL RECORD TO THE EXISTING TYPE OBJECT
                                        sortedSchools[thisSubType].push(schoolList[type]);
                                    }
                                }
                            }

                            // SINGLE TYPE VALUE
                            else {
                                // ADD THIS TYPE OBJECT TO THE SORTED SCHOOLS OBJECT IF IT'S NOT THERE YET
                                if(!sortedSchools.hasOwnProperty(thisType)) {
                                    // ADD THIS TYPE VALUE TO THE SORTED SCHOOLS OBJECT AND ADD THIS SCHOOL RECORD
                                    sortedSchools[thisType] = [schoolList[type]];
                                } else {
                                    // PUSH THIS SCHOOL RECORD TO THE EXISTING TYPE OBJECT
                                    sortedSchools[thisType].push(schoolList[type]);
                                }
                            }
                        }
                    }

                    for(var i in schoolList) {
                        if(schoolList[i].schoolVisible.toLowerCase() == 'true' && schoolList[i].schoolLogo != "") {
                            schools += '
' + schoolList[i].schoolName + '';
                        } else if(schoolList[i].schoolVisible.toLowerCase() == 'true') {
                            schools += '
' + schoolList[i].schoolName + '';
                        }
                    }

                    // ADD THE COMPLETED MENU TO THE DOM
                    $("#gb-school-list-inner ul").html(schools);

                    // BIND THE TAB PANEL EVENTS
                    template.SchoolMenuControls();

                    template.RsMenu(sortedSchools);
                }
            });
		},
        "SchoolMenuControls": function() {
        	// FOR SCOPE
            var template = this;

        	// SCHOOLS BUTTON CLICK/KEYDOWN
            $("#gb-school-list button").on("click keydown", function(e) {
                if(template.AllyClick(e)) {
                    e.preventDefault();

                    // MENU IS NOT OPEN
                    if(!$(this).parent().hasClass("open")) {
                        // OPEN THE MENU
                        $("#gb-school-list-outer").fadeIn().attr("aria-hidden", "false");

						if(e.type == "keydown") {
                        	$("#gb-school-list-inner a").eq(0).focus();
                        }
                        $("#gb-school-list-inner a").attr("tabindex", 0);

                        // SET ATTRIBUTES
                        $("body").addClass("school-list-overflow");
                        $(this).parent().addClass("open").find("button").attr("aria-expanded", "true");

                    } else if($(this).parent().hasClass("open")) {
                        // CLOSE THE MENU
                        $("#gb-school-list-outer").fadeOut(function() {
                            // SET ATTRIBUTES
                            $("body").removeClass("school-list-overflow");
                        }).attr("aria-hidden", "true");
                        $("#gb-school-list-inner a").attr("tabindex", -1);

                        // SET ATTRIBUTES
                        $(this).parent().removeClass("open").find("button").attr("aria-expanded", "false");
                    }
                }
            });

            // CLOSE MENU
            $("#gb-close-school-menu").on("click keydown", function(e) {
            	if(template.AllyClick(e)) {
                	e.preventDefault();

                	// CLOSE THE MENU
                    $("#gb-school-list-outer").fadeOut(function() {
                        // SET ATTRIBUTES
                        $("body").removeClass("school-list-overflow");
                    }).attr("aria-hidden", "true");
                    $("#gb-school-list-inner a").attr("tabindex", -1);

                    // SET ATTRIBUTES
                    $("#gb-school-list").removeClass("open").find("button").attr("aria-expanded", "false").focus();
                } if(e.keyCode == template.KeyCodes.tab && e.shiftKey) {
                    e.preventDefault();

                    $("#gb-school-list-inner li:last-child a").focus();
                }
            });

            // SHIFT+TAB FROM FIRST LINK
            $(document).on("keydown", "#gb-school-list-inner li:first-child a", function(e) {
            	if(e.keyCode == template.KeyCodes.tab && e.shiftKey) {
                    e.preventDefault();

                    $("#gb-close-school-menu").focus();
                }
            });

            $(document).on("keydown", "#gb-school-list-inner li:last-child a", function(e) {
            	if(e.keyCode == template.KeyCodes.tab) {
                    e.preventDefault();

                    $("#gb-close-school-menu").focus();
                }
            });

            // TAB FROM CLOSE BUTTON
            $(document).on("keydown", "#gb-close-school-menu", function(e) {
            	if(e.keyCode == template.KeyCodes.tab && !e.shiftKey) {
                    e.preventDefault();

                    $("#gb-school-list-inner li:first-child a").focus();
                }
            });

            // LINK ARROW CONTROLS
            $(document).on("keydown", "#gb-school-list-inner a", function(e) {
            	var totalNum = $("#gb-school-list-inner a").length;

            	switch(e.keyCode) {
                	case template.KeyCodes.up:
                    case template.KeyCodes.left:
                    	e.preventDefault();

						if($(this).parent().index() == 0) {
                        	$("#gb-close-school-menu").focus();
                        } else {
                        	$(this).parent().prev().find("a").focus();
                        }
                    break;

                    case template.KeyCodes.down:
                    case template.KeyCodes.right:
                    	e.preventDefault();

                        if($(this).parent().index() == totalNum) {
                        	$("#gb-close-school-menu").focus();
                        } else {
                        	$(this).parent().next().find("a").focus();
                        }
                    break;
                }
            });

            // CLOSE MENU WITH ESC
            $(document).on("keydown", "#gb-school-list-outer", function(e) {
                if(e.keyCode == template.KeyCodes.esc) {
                    e.preventDefault();

                    // CLOSE THE MENU
                    $("#gb-school-list-outer").fadeOut(function() {
                        // SET ATTRIBUTES
                        $("body").removeClass("school-list-overflow");
                    }).attr("aria-hidden", "true");
                    $("#gb-school-list-inner a").attr("tabindex", -1);

                    // SET ATTRIBUTES
                    $(".cs-mystart-button.schools").removeClass("open").find("button").attr("aria-expanded", "false").focus();
                }
            });
        },
        "Header": function() {
        	//FOR SCOPE
            var template = this;

        	//ADA Options
            $("#gb-a11y-options-btn").on("click keydown", function(e) {
            	if(template.AllyClick(e)) {
                    e.preventDefault();
                    if($(this).attr("aria-expanded") == "true") {
                    	$(this).attr("aria-expanded", "false");
                        $("#gb-a11y-options-list").attr("aria-hidden", "true").slideUp(250);
                    } else {
                    	$(this).attr("aria-expanded", "true");
                        $("#gb-a11y-options-list").attr("aria-hidden", "false").slideDown(250);
                        $("#custom-contrast button:eq(0)").focus();
                    }
                }
            });

            //User Menu Options
            /*$("#gb-user-options-btn").on("mouseover", function() {
            	$("#gb-channel-list").attr({"aria-hidden": false, "aria-expanded": true}).show("slide", { direction: "right" }, 250);
            });

            $("#gb-header-outer").on("mouseleave", function() {
            	$("#gb-channel-list").attr({"aria-hidden": true, "aria-expanded": false}).hide("slide", { direction: "right" }, 250);
            });*/

            $("#gb-user-options-btn").on("click keydown", function(e) {
            	if(template.AllyClick(e)) {
                	e.preventDefault();
                    if(e.keyCode == template.KeyCodes.enter) {
                        if($(this).hasClass("open")) {
                            $(this).removeClass("open");
                            $("#gb-search-btn").attr("aria-expanded", "false");
                            $("#gb-search-form").attr("aria-hidden", "true").hide("slide", { direction: "right" }, 250);
                            $("#gb-custom-mystart").attr({"aria-hidden": true, "aria-expanded": false}).slideUp(250);
                        } else {
                            $(this).addClass("open");
                            $("#gb-custom-mystart").attr({"aria-hidden": false, "aria-expanded": true}).slideDown(250, function() {
                            	$("#gb-search-btn").focus();
                            });
                        }
                    } else if(e.keyCode == template.KeyCodes.space) {
                    	if($("#gb-channel-list[aria-hidden='true']").length) {
                          $("#gb-channel-list").attr({"aria-hidden": false, "aria-expanded": true}).show("slide", { direction: "right" }, 250, function(){ 
                              $("#channel-navigation > li:eq(0) a").focus();
                          });
                        } else {
                           $("#gb-channel-list").attr({"aria-hidden": true, "aria-expanded": false}).hide("slide", { direction: "right" }, 250);
                        }
                    } else {
                        if($(this).hasClass("open")) {
                            $(this).removeClass("open");
                            $("#gb-search-btn").attr("aria-expanded", "false");
                            $("#gb-search-form").attr("aria-hidden", "true").hide("slide", { direction: "right" }, 250);
                            $("#gb-custom-mystart").attr({"aria-hidden": true, "aria-expanded": false}).slideUp(250);
                        } else {
                            $(this).addClass("open");
                            $("#gb-custom-mystart").attr({"aria-hidden": false, "aria-expanded": true}).slideDown(250);
                        }
                    }
                }
            });

            // ADD LOGO
            var showLogo = true;
            var logoSrc = $.trim("/cms/lib/NJ01912805/Centricity/Template/GlobalAssets/images///logos/MTPSlogo.png");
            if(showLogo) {
                var srcSplit = logoSrc.split("/");
                var srcSplitLen = srcSplit.length;
                if((logoSrc == "") || (srcSplit[srcSplitLen - 1] == "default-man.jpg")) {
                   	logoSrc = this.DefaultLogoSrc; //place default logo src here!
                }

                $("#gb-logo").prepend('');
            }
        },
        "StickyHeader": function() {
			if(!this.IsMyViewPage) {
                switch(this.GetBreakPoint()) {
                    case "desktop":
                        var navOffSet = $("main").offset().top;
                        var windowWidth = $(window).width();

                        if ($(window).scrollTop() <= navOffSet) {
                            if(this.HeaderIsSticky) {
                                $("header").removeClass("sticky").removeAttr("style");
                                this.HeaderIsSticky = false;
                            }
                        } else {
                        	$("header").css("height", $("header").outerHeight(true));

                            if(!this.HeaderIsSticky) {
                                $("header").addClass("sticky");
                                this.HeaderIsSticky = true;
                            }

                            if($(".sp").length && $(".ui-widget.app.calendar").length) {
                            	if($(".wcm-controls").hasClass("wcm-stuck")) {
                                	$(".wcm-controls").css("margin-top", $("#gb-header-outer").outerHeight(true));
                                }
                            }
                        }

                        $("#sw-maincontent").css({
                            "display": "block",
                            "position": "relative",
                            "top": "-" + $("#gb-header-wrapper").outerHeight(true) + "px"
                        });
                    break;
                    case "768": case "640": case "480": case "320":
                        if(this.HeaderIsSticky) {
                            $("header").removeClass("sticky").removeAttr("style");

                            this.HeaderIsSticky = false;
                        }

                        if($(".sp").length && $(".ui-widget.app.calendar").length) {
                            $(".wcm-controls").css("margin-top", "0px");
                        }
                        $("#sw-maincontent").removeAttr("style");
                    break;
                }
            }
        },
