        "Search": function() {
            // FOR SCOPE
            var _this = this;

            // OPEN SEARCH DIALOG
            $(document).on("click keydown", "#gb-search-btn", function(e) {            
                if(_this.AllyClick(e)) {
                	e.preventDefault();
                    if($(this).attr("aria-expanded") == "false") {
                    	$(this).attr("aria-expanded", "true");
                        $("#gb-search-form").attr("aria-hidden", "false").show("slide", { direction: "right" }, 250);

                        setTimeout(function() {
                            $("#gb-search-submit-btn").focus();
                        }, 260);
                    } else {
                    	$(this).attr("aria-expanded", "false");
                        $("#gb-search-form").attr("aria-hidden", "true").hide("slide", { direction: "right" }, 250);
                    }
                } else if(e.keyCode == _this.KeyCodes.tab) {
                        $(this).attr("aria-expanded", "false");
                        $("#gb-search-form").attr("aria-hidden", "true").hide("slide", { direction: "right" }, 250);
                }
            });

            $("#gb-search-form").on('focusout', function(e) {
                var $elem = $(this);
                setTimeout(function() {
                    var hasFocus = !! ($elem.find(':focus').length > 0);
                    var hasFocus2 = !! ($("#gb-search-btn:focus").length > 0);

                    if (! hasFocus && ! hasFocus2) {
                    	$("#gb-search-btn").attr("aria-expanded", "false");
                        $("#gb-search-form").attr("aria-hidden", "true").hide("slide", { direction: "right" }, 250);
                    }
                }, 10);
            });

            $("#gb-search-form form").submit(function(e){
				e.preventDefault();
				window.location.href = "/site/Default.aspx?PageType=6&SiteID=4&SearchString=" + $("#gb-search-input").val();
			});

            $(document).on("keydown", "#gb-search-input", function(e) {
                if(e.keyCode == _this.KeyCodes.tab) {
                	e.preventDefault();
                    $("#gb-search-btn").focus();
                }
            });
        },
        "SmoothScroll": function(to, offset){
        	/*
            ** smoothScroll
            ** Use: This will smoothly scroll to the id that was passed to the function.
            ** Return: N/A
            ** Parameter(s): to (jQuery "#id") The id of the section to be scolled to.
            ** 		offset (int) The amount of space you want to be left on the top of the element after scroll (in a whole number int value)
            */
        	if( to.indexOf("#") < 0 ){ to = "#" + to; } //makes sure we have a valid id accessor string
            var target = $(to);

            if (target.length) {
                $('html,body').animate({
                    scrollTop: target.offset().top - offset //this offset accounts for the fixed menu on the top
                }, 800, function(){
                    $("html, body").stop(true, false);
                });
                return false;
            }
        },

