        "RsMenu": function(sortedSchools) {
			// FOR SCOPE
			var _this = this;

            // ADDITIONAL ITEMS OBJECT
            var additionalItems = {};

            $.each(sortedSchools, function(type, schools) {
                var schoolsData = [];
                var typeName = type;

                // SORT SCHOOLS ARRAY ALPHABETICALLY IN CASE SCHOOLS GOT SHIFTED A BIT FROM MULTIPLE TYPE VALUES
                schools.sort(function(a, b) {
                    if(a.schoolName > b.schoolName) {
                        return 1;
                    }
                    if(a.schoolName < b.schoolName) {
                        return -1;
                    }
                    return 0;
                });

                for(var school in schools) {
                    schoolsData.push({ "text": schools[school].schoolName, "url": schools[school].schoolUrl });
                }

                additionalItems[typeName] = schoolsData;
            });

            // GLOBAL ICONS
            var globalIconsData = [
                {
                    "text": "Food Services",
                    "url": "https://www.middletownk12.org/Page/11481",
                },
                {
                    "text": "PowerSchool Parent Portal",
                    "url": "https://www.middletownk12.org/domain/61",
                },
                {
                    "text": "Calendar",
                    "url": "https://www.middletownk12.org/Page/2",
                },
                {
                    "text": "Registration",
                    "url": "https://www.middletownk12.org/domain/57",
                },
                {
                    "text": "Athletics",
                    "url": "https://www.middletownk12.org/Page/9749",
                },
                {
                    "text": "Transportation",
                    "url": "https://www.middletownk12.org/Page/269",
                }
            ];

            var haveGlobalIcons = false;
            var globalIcons = [];
            var globalIconNum = parseInt("6");
            if(globalIconNum == NaN) {
            	if('6' == "4 - Default") {
                	globalIconNum = 4;
                } else {
					var splitDefaultVal = globalIconNum.split(";");
					globalIconNum = parseInt(splitDefaultVal[0]);
                }
			}

            for(var g = 0; g < globalIconNum; g++) {
                globalIcons.push({ "text": globalIconsData[g].text, "url": globalIconsData[g].url });

                haveGlobalIcons = true;
            }

            if(haveGlobalIcons) {
            	additionalItems["Global Icons"] = globalIcons;
            }

            $.csRsMenu({
                "breakPoint" : 768, // SYSTEM BREAK POINTS - 768, 640, 480, 320
                "slideDirection" : "right-to-left", // OPTIONS - left-to-right, right-to-left
                "menuButtonParent" : ".gb-header.right",
                "menuBtnText" : "Menu",
                "colors": {
                    "pageOverlay": "#000000", // DEFAULT #000000
                    "menuBackground": "#FFFFFF", // DEFAULT #FFFFFF
                    "menuText": "#333333", // DEFAULT #333333
                    "menuTextAccent": "#333333", // DEFAULT #333333
