                $(".upcomingevents .ui-articles .ui-article").each(function(){
                    $(".ui-article-title", this).appendTo($(this));

                    $(".sw-calendar-block-title", this).each(function(){
                    	$(this).prependTo($(this).parent());
                    });

                    $(".joel-day", this).append(" ").prependTo($(".ui-article-description:last .sw-calendar-block-time", this));
                });

                // ADD 0 TO EVENT DATES IF ONLY SINGLE DIGITS
                $(".ui-widget.app.upcomingevents .sw-calendar-block-date").each(function() {
                    var text = $(this).text();

                    if(text.length > 5) {
                    	text = $.trim(text.substr(text.length - 5));
                        var month = text.substring(0, 3);
                    	var date = text.substring(3);
                    } else {
                    	var month = text.substring(0, 3);
                    	var date = text.substring(3);
                    }

                    if(date.length == 1) {
                        date = "0"+date;
                    }

                    $(this).html(''+month+''+date+'');
                });

                $(".hp-row.three .upcomingevents").each(function() {
                	$(".view-calendar-link", this).wrap('

');
                    $(".ui-widget-detail > li", this).appendTo($(".ui-articles", this));
                });
