        "ShortcutResources": function(){
            $(".hp-row.two .navigation").each(function() {
            	//Check header text for Resources and make it bold
                var headerText = $(".ui-widget-header", this).text();
                $(".ui-widget-header > *", this).html(headerText.replace(/Resources/gi, 'Resources'));
                $(".ui-widget-detail", this).attr("aria-hidden", true);
            });

            // CUSTOM HOMEPAGE SHORTCUTS DESCRIPTION
            $(".hp-row.two .navigation").bind("mouseenter", function() {
            	$(this).addClass("hover");
                $(".ui-widget-header", this).attr("aria-hidden", true).animate({
                    opacity: 0
                }, 250);
                $(".ui-widget-detail", this).attr("aria-hidden", false).animate({
                    opacity: 1
                }, 250);
            });

            $(".hp-row.two .navigation a").bind("focus", function() {
            	var element = $(this).parents(".app");
            	$(element).addClass("hover");
                 $(".ui-widget-header", element).attr("aria-hidden", true).animate({
                    opacity: 0
                }, 250);
                $(".ui-widget-detail", element).attr("aria-hidden", false).animate({
                    opacity: 1
                }, 250);
            });

            $(".hp-row.two .navigation").bind("mouseleave", function() {
            	$(this).removeClass("hover");
                $(".ui-widget-header", this).attr("aria-hidden", false).animate({
                    opacity: 1
                }, 250);
                $(".ui-widget-detail", this).attr("aria-hidden", true).animate({
                    opacity: 0
                }, 250);
            });

            $(".hp-row.two .navigation").bind("focusout", function() {
            	var element = $(this); 
                setTimeout(function() {
                    var hasFocus = !! (element.find(':focus').length > 0);
                    if(!hasFocus) {
                        $(element).removeClass("hover");
                        $(".ui-widget-header", element).attr("aria-hidden", false).animate({
                            opacity: 1
                        }, 250);
                        $(".ui-widget-detail", element).attr("aria-hidden", true).animate({
                            opacity: 0
                        }, 250);
                    }
                }, 10);
            });
        },
        "ContrastOptions": function(){
        	//FOR SCOPE
            var _this = this;

            //CONTRAST OPTIONS
            $(".contrast.black-yellow").on("click keydown", function(e) {
            	if(_this.AllyClick(e)) {
                    e.preventDefault();
                    _this.eraseCookie("whiteBlack");
                    _this.eraseCookie("blackWhite");
                    if($("body").hasClass("contrast-blackYellow")) {
                        $("body").removeClass("contrast-blackYellow");
                        _this.eraseCookie("blackYellow");
                    } else {
                        className = "contrast-blackYellow";
                        $("body").removeClass("contrast-whiteBlack");
                        $("body").removeClass("contrast-blackWhite");
                        $("body").toggleClass(className);
                        _this.createCookie("blackYellow",className);
                        return false;
                    }
                }
            });

            $(".contrast.white-black").on("click keydown", function(e) {
            	if(_this.AllyClick(e)) {
                    e.preventDefault();
                    _this.eraseCookie("blackYellow");
                    _this.eraseCookie("blackWhite");
                    if($("body").hasClass("contrast-whiteBlack")) {
                        $("body").removeClass("contrast-whiteBlack");
                        _this.eraseCookie("whiteBlack");
                    } else {
                        className = "contrast-whiteBlack";
                        $("body").removeClass("contrast-blackYellow");
                        $("body").removeClass("contrast-blackWhite");
                        $("body").toggleClass(className);
                        _this.createCookie("whiteBlack",className);
                        return false;
                    }
                }
            });

            $(".contrast.black-white").on("click keydown", function(e) {
                if(_this.AllyClick(e)) {
                    e.preventDefault();
                    _this.eraseCookie("blackYellow");
                    _this.eraseCookie("whiteBlack");
                    if($("body").hasClass("contrast-blackWhite")) {
                        $("body").removeClass("contrast-blackWhite");
                        _this.eraseCookie("blackWhite");
                    } else {
                        className = "contrast-blackWhite";
                        $("body").removeClass("contrast-blackYellow");
                        $("body").removeClass("contrast-whiteBlack");
                        $("body").toggleClass(className);				
                        _this.createCookie("blackWhite",className);
                        return false;
                    }
                }
            });

            if(_this.readCookie("blackYellow") != null) {
            	$("body").removeClass("contrast-whiteBlack");
            	$("body").removeClass("contrast-blackWhite");
                $("body").addClass(_this.readCookie("blackYellow"));

            } else if (_this.readCookie("whiteBlack") != null) {
            	$("body").removeClass("contrast-blackYellow");
            	$("body").removeClass("contrast-blackWhite");
                $("body").addClass(_this.readCookie("whiteBlack"));

            } else if (_this.readCookie("blackWhite") != null) {
                $("body").removeClass("contrast-blackYellow");
                $("body").removeClass("contrast-whiteBlack");
                $("body").addClass(_this.readCookie("blackWhite"));
            } else {
                $("body").removeClass("contrast-blackYellow");
                $("body").removeClass("contrast-whiteBlack");
                $("body").removeClass("contrast-blackWhite");
            }

            //TEXT SIZE OPTIONS
            $("#custom-text-size .text.default").on("click keydown", function(e) {
                if(_this.AllyClick(e)) {
                    e.preventDefault();
                    $("body").removeClass("text-medium");
                    $("body").removeClass("text-large");
                    _this.eraseCookie("textMedium");
                    _this.eraseCookie("textLarge");
                }
            });

            $("#custom-text-size .text.medium").on("click keydown", function(e) {
                if(_this.AllyClick(e)) {
                    e.preventDefault();
                    var textSize = "text-medium";
                    $("body").removeClass("text-large");
                    $("body").toggleClass(textSize);
                    _this.createCookie("textMedium",textSize);
                    return false;
				}
            });

            $("#custom-text-size .text.large").on("click keydown", function(e) {
                if(_this.AllyClick(e)) {
                    e.preventDefault();
                    var textSize = "text-large";
                    $("body").removeClass("text-medium");
                    $("body").toggleClass(textSize);
                    _this.createCookie("textLarge",textSize);
                    return false;
                }
            });

            if (_this.readCookie("textMedium") != null) {
            	$("body").removeClass("text-large");
                $("body").addClass(_this.readCookie("textMedium"));

            } else if(_this.readCookie("textLarge") != null) {
            	$("body").removeClass("text-medium");
                $("body").addClass(_this.readCookie("textLarge"));

            } else {
                $("body").removeClass("text-medium");
            	$("body").removeClass("text-large");
            }
        },
        "createCookie": function(name, value, days) {
            var expires;

            if (days) {
                var date = new Date();
                date.setTime(date.getTime() + (days * 24 * 60 * 60 * 1000));
                expires = "; expires=" + date.toGMTString();
            } else {
                expires = "";
            }
            document.cookie = encodeURIComponent(name) + "=" + encodeURIComponent(value) + expires + "; path=/";
        },
        "readCookie": function(name) {
            var nameEQ = encodeURIComponent(name) + "=";
            var ca = document.cookie.split(';');
            for (var i = 0; i < ca.length; i++) {
                var c = ca[i];
                while (c.charAt(0) === ' ') c = c.substring(1, c.length);
                if (c.indexOf(nameEQ) === 0) return decodeURIComponent(c.substring(nameEQ.length, c.length));
            }
            return null;
        },
        "eraseCookie": function(name) {
            this.createCookie(name, "", -1);
        }
	};

