        "DropdownActions": function(params) {
            // FOR SCOPE
            var template = this;

            var dropdownParent = params.dropdownParent;
            var dropdownSelector = params.dropdownSelector;
            var dropdown = params.dropdown;
            var dropdownList = params.dropdownList;

            $(dropdownParent + " " + dropdownList + " a").attr("tabindex", "-1");

            // MYSTART DROPDOWN SELECTOR CLICK EVENT
            $(dropdownParent).on("click", dropdownSelector, function(e) {
                e.preventDefault();

                if($(this).parent().hasClass("open")){
                    $("+ " + dropdownList + " a").attr("tabindex", "-1");
                    $(this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing"); 
                } else {
                    $(this).attr("aria-expanded", "true").parent().addClass("open").find(dropdown).attr("aria-hidden","false").slideDown(300, "swing");
                }
            });

            // MYSTART DROPDOWN SELECTOR KEYDOWN EVENTS
            $(dropdownParent).on("keydown", dropdownSelector, function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.enter:
                    case template.KeyCodes.space:
                        e.preventDefault();

                        // IF THE DROPDOWN IS OPEN, CLOSE IT
                        if($(dropdownParent).hasClass("open")){
                            $("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                            $(this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing"); 
                        } else {
                            $(this).attr("aria-expanded", "true").parent().addClass("open").find(dropdown).attr("aria-hidden", "false").slideDown(300, "swing", function(){
                                $(dropdownList + " li:first-child a", this).attr("tabindex", "0").focus();
                            });
                        }
                    break;

                    // CONSUME TAB KEY
                    case template.KeyCodes.tab:
                        if($("+ " + dropdown + " " + dropdownList + " a").length) {
                            $("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                            $(this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
                        }
                    break;

                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.down:
                    case template.KeyCodes.right:
                        e.preventDefault();

                        $("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                        $("+ " + dropdown + " " + dropdownList + " li:first-child > a", this).attr("tabindex", "0").focus();
                    break;
                }
            });

            // MYSTART DROPDOWN LINK KEYDOWN EVENTS
            $(dropdownParent).on("keydown", dropdownList + " li a", function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.left:
                    case template.KeyCodes.up:
                        e.preventDefault();

                        // IS FIRST ITEM
                        if($(this).parent().is(":first-child")) {
                            // FOCUS DROPDOWN BUTTON
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).closest(dropdownParent).find(dropdownSelector).focus();
                        } else {
                            // FOCUS PREVIOUS ITEM
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).parent().prev("li").find("> a").attr("tabindex", "0").focus();
                        }
                    break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case template.KeyCodes.right:
                    case template.KeyCodes.down:
                        e.preventDefault();

                        // IS LAST ITEM
                        if($(this).parent().is(":last-child")) {
                            // FOCUS FIRST ITEM
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).closest(dropdownList).find("li:first-child > a").attr("tabindex", "0").focus();
                        } else {
                            // FOCUS NEXT ITEM
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).parent().next("li").find("> a").attr("tabindex", "0").focus();
                        }
                    break;

                    // CONSUME TAB KEY
                    case template.KeyCodes.tab:
                        if(e.shiftKey) {
                            e.preventDefault();

                            // FOCUS DROPDOWN BUTTON
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).closest(dropdownParent).find(dropdownSelector).focus();
                        }
                    break;

                    // CONSUME HOME KEY
                    case template.KeyCodes.home:
                        e.preventDefault();

                        // FOCUS FIRST ITEM
                        $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                        $(this).closest(dropdownList).find("li:first-child > a").attr("tabindex", "0").focus();
                    break;

                    // CONSUME END KEY
                    case template.KeyCodes.end:
                        e.preventDefault();

                        // FOCUS LAST ITEM
                        $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                        $(this).closest(dropdownList).find("li:last-child > a").attr("tabindex", "0").focus();
                    break;

                    // CONSUME ESC KEY
                    case template.KeyCodes.esc:
                        e.preventDefault();

                        // FOCUS DROPDOWN BUTTON
                        $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                        $(this).closest(dropdownParent).find(dropdownSelector).focus();
                        $(this).closest(dropdownParent).removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
                   break;
                }
            });

            $(dropdownParent).mouseleave(function(e) {
                if(e.target.nodeName.toLowerCase() !== "select") {
                    $(dropdownList + " a", this).attr("tabindex", "-1");
                    $(dropdownSelector, this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing"); 
                }
            }).focusout(function() {
                var thisDropdown = this;

                setTimeout(function () {
                    if(!$(thisDropdown).find(":focus").length) {
                        $(dropdownSelector, thisDropdown).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
                    }
                }, 500);
            });
        },

        "Header": function() {
            // ADD LOGO
            var logoSrc = jQuery.trim("/cms/lib/NJ01001510/Centricity/Template/GlobalAssets/images///logos/northvalelogo2.png");
            var srcSplit = logoSrc.split("/");
            var srcSplitLen = srcSplit.length;
            if((logoSrc != "") && (srcSplit[srcSplitLen - 1] != "default-man.jpg")) {
                $("#gb-logo").append("").removeClass("hidden");
            } else {
                $("#gb-logo").append("").removeClass("hidden");
            }

            if("Northvale Public School" == "") {
            	$("#gb-sitename h1").remove();
            }
        },

        "ChannelBar": function() {
        	//ALIGN CHANNELS IF CHANNEL BAR WRAPS
            if($("#gb-channel-list").height() > 35){
            	$("#gb-channel-list").addClass("wrapped-channels");
            }

            $(".sw-channel-item").unbind('hover');
            $(".sw-channel-item").hover(function(){
                $(".sw-channel-item ul").stop(true, true);
                var subList = $(this).children('ul');
                if ($.trim(subList.html()) !== "") {
                    subList.slideDown(300, "swing");
                }
                $(this).addClass("hover");
            }, function(){
                $(".sw-channel-dropdown").slideUp(300, "swing");
                $(this).removeClass("hover");
            });

            $(".sw-channel-item").each(function(){
                if($(".sw-channel-dropdown li", this).length == 0) {
                    $(this).addClass("no-dropdown");
                } else {
                	$(this).addClass("dropdown");
                }
            });

        },

        "StickyChannelBar": function() {

            var distanceToNav = $("#gb-channel-list").offset().top;
            distanceToNav = distanceToNav + $("#gb-channel-list").outerHeight(true);

            $(window).scroll(function() {
            	var navHeight = $("#gb-channel-list").outerHeight();
            	var distanceFromTop = $(window).scrollTop();

            	if(distanceFromTop >= distanceToNav){
                    $("#gb-page").addClass("sticky-nav");
                } else {
                    $("#gb-page").removeClass("sticky-nav");
                }

                if($(".sp").length && $(".ui-widget.app.calendar").length) {
                    if($(".wcm-controls").hasClass("wcm-stuck")) {
                         $(".wcm-controls").css("margin-top", navHeight);
                    }
                }
            });

             $("#sw-maincontent").css({
                    "display": "block",
                    "position": "relative",
                    "top": "-" + $("#gb-channel-list").outerHeight(true) + "px"
                });
        },

        "StickyMenu": function() {
        	if(!this.IsMyViewPage) {
                switch(this.GetBreakPoint()) {
                    case "768": case "640": case "480": case "320":
                    var distanceToNav = $(".header-column.two").offset().top;

                    var navHeight = $(".header-column.two").outerHeight();
                        var distanceFromTop = $(window).scrollTop();

                        if(distanceFromTop >= distanceToNav){
                            $("#gb-page").addClass("sticky-nav");
                        } else {
                            $("#gb-page").removeClass("sticky-nav");
                        }

                        if($(".sp").length && $(".ui-widget.app.calendar").length) {
                            if($(".wcm-controls").hasClass("wcm-stuck")) {
                                 $(".wcm-controls").css("margin-top", navHeight);
                            }
                        }

                    $(window).scroll(function() {
                        var navHeight = $(".header-column.two").outerHeight();
                        var distanceFromTop = $(window).scrollTop();

                        if(distanceFromTop >= distanceToNav){
                            $("#gb-page").addClass("sticky-nav");
                        } else {
                            $("#gb-page").removeClass("sticky-nav");
                        }

                        if($(".sp").length && $(".ui-widget.app.calendar").length) {
                            if($(".wcm-controls").hasClass("wcm-stuck")) {
                                 $(".wcm-controls").css("margin-top", navHeight);
                            }
                        }
                     });
                    break;
                }
            }
        },

        "Body": function() {
            // FOR SCOPE
            var _this = this;

            // ADD CLASS TO NAV IF DASHBOARD IS ACTIVE
            if($("#dashboard-sidebar").length) {
                $("nav, .footer-row.three").addClass("dashboard-active");
            }

            //EVENT HOVERS
            $(".upcomingevents .sw-calendar-block-title a").hover(function(){
                $(this).closest("li").addClass("hover");
            }, function(){
                $(this).closest("li").removeClass("hover");
            })

            $(".upcomingevents .sw-calendar-block-title a").on("focus", function(){
                $(this).closest("li").addClass("hover");
            })

            $(".upcomingevents .sw-calendar-block-title a").on("focusout", function(){
                $(this).closest("li").removeClass("hover");
            })


            //HP EVENTS ARTICLE SLIDERS
            $("[data-region='b'] .upcomingevents, [data-region='e'] .upcomingevents").csArticleSlider({
                "viewerNum": [5, 3, 2, 2, 1], // NUMERICAL - [960 view, 768 view, 640 view, 480 view, 320 view]
                "transitionSpeed": .5 // SECONDS
            });
            $('[data-region="b"] .upcomingevents .ui-widget-detail, [data-region="e"] .upcomingevents .ui-widget-detail').prepend("
").append("
");

            if($("[data-region='cd'] .app").length){
                $("[data-region='cd']").show();
            }

            //MOVE MORE LINKS INTO FOOTER
            $("[data-region='b'] .app:not(.headlines), [data-region='e'] .app:not(.headlines), [data-region='cd'] .app, [data-region='sp2'] .app").each(function(){
                if($(this).find(".more-link").length){
                    $(this).find(".more-link").prependTo($(this).find(".ui-widget-footer").addClass("has-more"));
                }
            })

            // AUTO FOCUS SIGN IN FIELD
            $("#swsignin-txt-username").focus();

            // APPLY RESPONSIVE DIMENSIONS TO CONTENT IMAGES
            $(".ui-widget.app .ui-widget-detail img")
                .not($(".ui-widget.app.multimedia-gallery .ui-widget-detail img"))
                .each(function() {
                    if ($(this).attr('width') !== undefined && $(this).attr('height') !== undefined) { // IMAGE HAS INLINE DIMENSIONS
                        $(this).css({"display": "inline-block", "width": "100%", "max-width": $(this).attr("width") + "px", "height": "auto", "max-height": $(this).attr("height") + "px"});
                    }
            });

            // HIDE REGION E IF NO APPS
            if(!$('[data-region="e"] .ui-widget.app').length) {
            	$('[data-region="e"]').remove();
            }

            // ADJUST FIRST BREADCRUMB
            $("li.ui-breadcrumb-first > a > span").text("Home").show();

            // SUBPAGE
            // USE CHANNEL NAME FOR PAGE NAV HEADER IF ONE DOESN'T EXIST
            if(!$("div.sp-column.one .ui-widget-header h1").length > 0) {
                $("div.sp-column.one .ui-widget-header").append("
");
            }

        },

        "HomepageHeadlines": function(){
            if($("#gb-page.hp").length){
                var defaultHeadlinesSrc = jQuery.trim('/cms/lib/NJ01001510/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg');
                var defaultHeadlinesSrcSplit = defaultHeadlinesSrc.split("/");
                var defaultHeadlinesSrcSplitLength = defaultHeadlinesSrcSplit.length;

