        "CheckSlideshow": function() {
        	// FOR SCOPE
            var _this = this;

            if($(".hp").length && this.GetBreakPoint() != "desktop") {
                if ($(window).scrollTop() <= $("#hp-slideshow").offset().top + $("#hp-slideshow").height()) {
                    if(this.SlideshowDescFixed) {
                        $("#hp-slideshow").removeAttr("style");

                        this.SlideshowDescFixed = false;
                    }
                } else {
                    if(!this.SlideshowDescFixed) {
                        $("#hp-slideshow").css({
                            "height": $("#hp-slideshow").height(),
                            "overflow": "hidden"
                        });

                        this.SlideshowDescFixed = true;
                    }
                }
            }
        },

        "StickySlideshow": function() {
            var headerHeight = $("#gb-header-outer").outerHeight(true);
            var slideshowHeight = headerHeight + $("#hp-slideshow-outer").outerHeight(true);

            if((slideshowHeight+55) < $(window).height()){
            	$("#hp-slideshow-outer").addClass("short-screen");
            } else {
            	$("#hp-slideshow-outer").removeClass("short-screen");
            }

            $(window).scroll(function() { 

            	var slideshowTop = $("#hp-slideshow-outer").offset().top;
                var slideshowBottom = slideshowTop + $("#hp-slideshow-outer").outerHeight(true);
                var scrollTop = $(window).scrollTop();
                var scrollBottom = scrollTop + $(window).height();

                if(scrollBottom <= (slideshowBottom + 55)) {
                    $("#hp-slideshow-outer").removeClass("unfixed");
                } else {
                    $("#hp-slideshow-outer").addClass("unfixed");
                }

				if(scrollBottom <= (slideshowHeight + 40)) {
                    $("#hp-slideshow-outer").removeClass("sm-fx-scroll");
                } else {
                    $("#hp-slideshow-outer").addClass("sm-fx-scroll");
                }

            });


        },

        "ModEvents": function() {
            $(".ui-widget.app.upcomingevents").modEvents({
                columns     : "yes",
                monthLong   : "yes",
                dayWeek     : "yes"
            });

            eventsByDay(".upcomingevents .ui-articles");

            function eventsByDay(container) {
                $(".ui-article", container).each(function(){
                    if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
                        var moveArticle = $(this).html();
                        $(this).parent().prev().children().children().next().append(moveArticle);
                        $(this).parent().remove();
                    };

                }); 

                $(".ui-article", container).each(function(){
                    var newDateTime = $('.upcoming-column.left h1', this).html().toLowerCase().split("");
                    var newDate = '';
                    var dateIndex = ((newDateTime.length > 2) ? 2 : 1); //if the dayWeek is set to yes we need to account for that in our array indexing

                    //add a zero to the date if it is a single digit
                    if( newDateTime[dateIndex].length == 1 ) newDateTime[dateIndex] = "0" + newDateTime[dateIndex];

                    //if we have the day of the week make sure to include it.
                    if( dateIndex > 1 ){ 
                        newDate += newDateTime[0] + "";
                    }

                    //add in the month
                    newDate += newDateTime[dateIndex - 1] + ''; //the month is always the in the left array position to the date

                    //wrap the date in a new tag
                    newDate += '' + newDateTime[dateIndex] + '';

                    //append the date and month back into their columns
                    $('.upcoming-column.left h1', this).html(newDate);  


                    //add an ALL DAY label if no time was given
                    $('.upcoming-column.right .ui-article-description', this).each(function(){
                        if( $('.sw-calendar-block-time', this).length < 1 ){ //if it doesnt exist add it
                            $(this).prepend('ALL DAY');
                        }
                    });

                    $(".joel-day, .joel-month", this).wrapAll("");

                })


            }

            $('.upcomingevents').each(function() {
               if(!$('.ui-article', this).length) {
                   $('.ui-articles',this).html('




There are no upcoming events to display.');
               }
            });

            // ADD VIEW MORE BUTTON IF DATE HAS MORE THAN 2 EVENTS
            $('[data-region="b"] .ui-widget.app.upcomingevents .upcoming-column.right, [data-region="e"] .ui-widget.app.upcomingevents .upcoming-column.right').each(function() {
            	var thisEventsCol = this;
                var thisEventsLeftCol = $(this).prev();
                var numEvents = $(".ui-article-description",thisEventsCol).length;
                if(numEvents > 2) {
                    var getDate = $(".sw-calendar-block-date",thisEventsLeftCol).text();
                	var eventUrl = $(".ui-article-description:first-child .sw-calendar-block-title a",thisEventsCol).attr("href");
                    var eventSplit = eventUrl.split("/");
                    var eventDayUrl = "/" + eventSplit[3] + "/" + eventSplit[4] + "/" + eventSplit[5] + "/day";
                	$("More Events for " + getDate + "").appendTo(thisEventsCol);
                    //$(".tabbed-region .upcoming-column-container .upcoming-column.right .ui-article-description:nth-child(1n + 3)").remove();
                }
            });
        },

