	function jsMediaQueries() {
		switch(csGetBreakPoint()) {
			case "desktop": 

			break;
			case "768":

			break;
			case "640":

			break;
			case "480":

			break;
			case "320":

			break;
		}
	}

	function alertMessage(){
		if($("#alert-container .ui-articles li").length){
			$("#alert-container").show();
		}

	}

	function accessibilityFunctions(){
		// KEY MAPPING
		var csKeyCodes = { "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 };


		//KEYBOARD ACCESSIBLE ICONS
		$(".header-icon.translate").keydown(function(e){

			//CAPTURE KEYCODE
			switch(e.keyCode){


				//CONSUME SPACE AND ENTER KEYS
				case csKeyCodes.enter:
				case csKeyCodes.space:

					e.preventDefault();

					//OPEN DROPDOWNS ON SPACE AND ENTER
					$(".sw-dropdown.translate").attr("aria-hidden","false").slideDown(300, "swing", function(){

						setTimeout(function(){
							$(".sw-dropdown.translate").focus();
						}, 100);
					});
					$(this).addClass("open");



				break;

				//CONSUME DOWN ARROW KEY
				case csKeyCodes.down:
					e.preventDefault();

					//SHOW THE MENU
					$(".sw-dropdown.translate").attr("aria-hidden","false");
					$(this).addClass("open");

					// FOCUS THE FIRST LINK
					$(".sw-dropdown.translate > li:first-child > a").focus();
				break;

				// CONSUME ESC KEY
			        case csKeyCodes.esc:
					e.preventDefault();

					// HIDE THE MENU
					$(".sw-dropdown.translate").attr("aria-hidden", "true").slideUp(300, "swing");
					$(this).removeClass("open");
					$(this).focus();
			        break;
			}
		});

		// DROPDOWN LINK KEYDOWN EVENTS
		$(".sw-dropdown.translate > li > a").keydown(function(e) {
			// CAPTURE KEY CODE
			switch(e.keyCode) {
				// CONSUME LEFT AND UP ARROWS
				case csKeyCodes.left:
				case csKeyCodes.up:
				e.preventDefault();

				// IS FIRST ITEM
				if($(this).parent().is(":first-child")) {
					// FOCUS DROPDOWN BUTTON
					$(".header-icon.translate").focus();
				} else {
					// FOCUS PREVIOUS ITEM
					$(this).parent().prev("li").find("> a").focus();
				}
				break;

				// CONSUME RIGHT AND DOWN ARROWS
				case csKeyCodes.right:
				case csKeyCodes.down:
				e.preventDefault();

				// IS LAST ITEM
				if($(this).parent().is(":last-child")) {
					// FOCUS FIRST ITEM
					$(".sw-dropdown.translate > li:first-child > a").focus();
				} else {
					// FOCUS NEXT ITEM

					$(this).parent().next("li").find("> a").focus();
					//alert("down");
					//$(this).parent().next().focus();
				}
				break;

				// CONSUME HOME KEY
				case csKeyCodes.home:
				e.preventDefault();

				// FOCUS FIRST ITEM
				$(".sw-dropdown.translate > li:first-child > a").focus();
				break;

				// CONSUME END KEY
				case csKeyCodes.end:
				e.preventDefault();

				// FOCUS LAST ITEM
				$(".sw-dropdown.translate > li:last-child > a").focus();
				break;

			}
		});

			$(".header-icon.account").keydown(function(e){

			//CAPTURE KEYCODE
			switch(e.keyCode){


				//CONSUME SPACE AND ENTER KEYS
				case csKeyCodes.enter:
				case csKeyCodes.space:

					e.preventDefault();

					//OPEN DROPDOWNS ON SPACE AND ENTER
					$(".account .sw-dropdown").attr("aria-hidden","false").slideDown(300, "swing", function(){

						setTimeout(function(){
							$(".account .sw-dropdown").focus();
						}, 100);
					});
					$(this).addClass("open");



				break;

				//CONSUME DOWN ARROW KEY
				case csKeyCodes.down:
					e.preventDefault();

					//SHOW THE MENU
					$(".account .sw-dropdown").attr("aria-hidden","false");
					$(this).addClass("open");

					// FOCUS THE FIRST LINK
					$(".account .sw-dropdown> li:first-child > a").focus();
				break;

				// CONSUME ESC KEY
			        case csKeyCodes.esc:
					e.preventDefault();

					// HIDE THE MENU
					$(".account .sw-dropdown").attr("aria-hidden", "true").slideUp(300, "swing");
					$(this).removeClass("open");
					$(this).focus();
			        break;
			}
		});

		// DROPDOWN LINK KEYDOWN EVENTS
		$(".account .sw-dropdown> li > a").keydown(function(e) {
			// CAPTURE KEY CODE
			switch(e.keyCode) {
				// CONSUME LEFT AND UP ARROWS
				case csKeyCodes.left:
				case csKeyCodes.up:
				e.preventDefault();

				// IS FIRST ITEM
				if($(this).parent().is(":first-child")) {
					// FOCUS DROPDOWN BUTTON
					$(".header-icon.account").focus();
				} else {
					// FOCUS PREVIOUS ITEM
					$(this).parent().prev("li").find("> a").focus();
				}
				break;

				// CONSUME RIGHT AND DOWN ARROWS
				case csKeyCodes.right:
				case csKeyCodes.down:
				e.preventDefault();

				// IS LAST ITEM
				if($(this).parent().is(":last-child")) {
					// FOCUS FIRST ITEM
					$(".account .sw-dropdown> li:first-child > a").focus();
				} else {
					// FOCUS NEXT ITEM

					$(this).parent().next("li").find("> a").focus();
					//alert("down");
					//$(this).parent().next().focus();
				}

				break;

				// CONSUME HOME KEY
				case csKeyCodes.home:
				e.preventDefault();

				// FOCUS FIRST ITEM
				$(".account .sw-dropdown> li:first-child > a").focus();
				break;

				// CONSUME END KEY
				case csKeyCodes.end:
				e.preventDefault();

				// FOCUS LAST ITEM
				$(".account .sw-dropdown > li:last-child > a").focus();
				break;

			}
		});

		$(".header-icon.search").keydown(function(e){

			//CAPTURE KEYCODE
			switch(e.keyCode){


				//CONSUME SPACE AND ENTER KEYS
				case csKeyCodes.enter:
				case csKeyCodes.space:

					e.preventDefault();

					$(this).toggleClass("open");
					$("#sw-mystart-search").toggleClass("open");
					if($("#sw-mystart-search").hasClass("open")){
						$("#sw-mystart-search").attr("aria-hidden", "false");
						setTimeout(function(){
							$("#sw-search-input").focus();
						}, 100);
					} else {
						$("#sw-mystart-search").attr("aria-hidden", "true");
						$(".header-icon.search").focus();
					}
				break;

			}
		});
	}

