              //GRAB CHANNEL NAME FOR IMAGE ALT TEXT
              var channelName = $("> a > span", this).text();

              //SETUP A CHECK FOR NORMAL DROPDOWNS
              var nothingSpecial = true;

              //WRAP ALL EXISTING SECTIONS IN THEIR OWN LI
              //$(".sw-channel-dropdown li", this).wrapAll("
");
				$(this).parent().find(".sw-channel-dropdown li").wrapAll("
");
				//console.log(channelElements[i][0]);
              //IF THERE IS AN IMAGE, PREPEND IT
              if(channelElements[i][1] != ''){
                  //CHECK TO SEE IF THEY ACTUALLY UPLOADED AN IMAGE
                 var channelDropdownImage = channelElements[i][1];

                 if(channelDropdownImage.indexOf("default-man") > 0 ){
                  	//console.log("no image");
                  } else {
                    //$(".sw-channel-dropdown", this).prepend("
");
                    $(this).parent().find(".sw-channel-dropdown").prepend("
");
                    $(this).parent().addClass("has-img");
                    nothingSpecial = false;
                  }

              }
              //IF THERE IS A DESCRIPTION, PREPEND IT
              if(channelElements[i][0] == '' || channelElements[i][0] == ' '){

              } else {
              	 //$(".sw-channel-dropdown", this).prepend("
"+channelElements[i][0]+"");
                 $(this).parent().find(".sw-channel-dropdown").prepend("
"+channelElements[i][0]+"");
                 $(this).parent().addClass("has-desc");
                  nothingSpecial = false;
              }

              //IF THERE IS NO IMAGE OR DESCRIPTION...
              if(nothingSpecial){
                  $(this).parent().addClass("no-img-desc");
              }

              //IF NO DROPDOWN AT ALL, ADD CLASS TO HIDE ACCENT ARROW ON HOVER
              //if(!$(".sw-channel-dropdown", this).length){
              if(!$(this).parent().find(".sw-channel-dropdown").length){
                  $(this).parent().addClass("no-dropdown");
              }

              if(count == 8){
              	$(this).parent().addClass("last");
              }

              count++;
            }
		});



	}

	function channelHeaderImage(){
    	if($("#sp-content-header").length){
        	//PLACE CHANNEL IMAGES INTO AN ARRAY
            var channelBgImages = ['','/cms/lib/NJ01912870/Centricity/Template/GlobalAssets/images///logos/Our District.jpg', '/cms/lib/NJ01912870/Centricity/Template/GlobalAssets/images///logos/Parents.jpg', '/cms/lib/NJ01912870/Centricity/Template/GlobalAssets/images///logos/Our Schools.jpg', '/cms/lib/NJ01912870/Centricity/Template/GlobalAssets/images///logos/Board and Community.jpg', '/cms/lib/NJ01912870/Centricity/Template/GlobalAssets/images///logos/departments.jpg', '/cms/lib/NJ01912870/Centricity/Template/GlobalAssets/images///logos/pemberton-logo.png', '/cms/lib/NJ01912870/Centricity/Template/GlobalAssets/images///logos/pemberton-logo.png'];

            //FIND THE ORDER OF THE ACTIVE CHANNEL IN THE CHANNEL BAR
            var activeChannelOrder = $(".sw-channel-item.active").index();
            var activeChannelText = $(".sw-channel-item.active span").text();


            defaultImage = "/cms/lib/NJ01912870/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg";
            bgImage = channelBgImages[activeChannelOrder];

            //CHECK FOR CALENDAR PAGE
            if(activeChannelText == 'Calendar' || activeChannelText == ""){
                $("#sp-content-header").css("background-image", "url('"+defaultImage+"')");
            } else {
                 //CHECK TO SEE IF THERE IS AN IMAGE FOR THE ACTIVE CHANNEL
                if (bgImage.indexOf("default-man") < 0){
                    $("#sp-content-header").css("background-image", "url('"+bgImage+"')");

                } else {
                    if (defaultImage.indexOf("default-man") < 0){
                        $("#sp-content-header").css("background-image", "url('"+defaultImage+"')");
                    }
                }
            } 
        }




	}

	function modEvents(){
		head.ready("twoColumnEvents", function(){
			if (!$(".upcomingevents .joel-month").length) {
		        $(".upcomingevents").modEvents({
		            columns     : "yes",
		            monthLong   : "no",
		            dayWeek     : "no"
		        });
		        eventsByDay(".upcomingevents .ui-articles");
		    }
		});
	}

	function eventsByDay(container) {
	    var countLi = 0;
	    $(".ui-article", container).each(function(){
	        if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
	            var moveArticle = $(this).html();
	            $(this).parent().prev().children().children().next().append(moveArticle);
	            $(this).parent().remove();
	        };
	    });




	    $(".ui-article", container).each(function(){
			var newDateTime = $('.upcoming-column.left h1', this).html().toLowerCase().split("");
			var newDate = '';
			var dateIndex = ((newDateTime.length > 2) ? 2 : 1); //if the dayWeek is set to yes we need to account for that in our array indexing

			//add a zero to the date if it is a single digit
			if( newDateTime[dateIndex].length == 1 ) newDateTime[dateIndex] = "0" + newDateTime[dateIndex];

			//if we have the day of the week make sure to include it.
			if( dateIndex > 1 ){ 
				newDate += newDateTime[0] + "";
			}

			//add in the month
			newDate += newDateTime[dateIndex - 1] + ''; //the month is always the in the left array position to the date

			//wrap the date in a new tag
			newDate += '' + newDateTime[dateIndex] + '';

			//append the date and month back into their columns
			$('.upcoming-column.left h1', this).html(newDate);	


			//add an ALL DAY label if no time was given
			$('.upcoming-column.right .ui-article-description', this).each(function(){
				if( $('.sw-calendar-block-time', this).length < 1 ){ //if it doesnt exist add it
					$(this).prepend('ALL DAY');
				}
			});
		});





	}

