	function dynamicContent() {
		// ********** MYSTART ********** //


		$("div#sw-mystart-search.sw-mystart-nav").unbind();
		$(document).off("click", ".sw-mystart-dropdown");
		$(document).on("click", ".header-icon-wrapper.account, .header-icon-wrapper.translate, .sw-mystart-dropdown", function(e) {
			$(".sw-dropdown", this).attr("aria-hidden","false").slideDown(300, "swing", function(){

				setTimeout(function(){
					$(".sw-dropdown", this).focus();
				}, 100);
			});
			$(this).addClass("open");

		});

		$(".header-icon-wrapper.translate").hover(function(e) {
			e.preventDefault();
		}, function() {
			setTimeout(function(){
            	$(".sw-dropdown.translate").attr("aria-hidden", "true").slideUp(300, "swing"); 
				$(".header-icon-wrapper.translate").removeClass("open");
            }, 250);

        });


        $(".header-icon-wrapper.account").hover(function(e) {
			e.preventDefault();
		}, function() {
			setTimeout(function(){
            	$("#sw-myaccount-list").attr("aria-hidden", "true").slideUp(300, "swing"); 
            	$(".header-icon-wrapper.account").removeClass("open");
        	}, 250);	

		});

		$(".header-icon.search").click(function(){

			$(this).toggleClass("open");
			$("#sw-mystart-search").toggleClass("open");
			if($("#sw-mystart-search").hasClass("open")){
				$("#sw-mystart-search").attr("aria-hidden", "false");
				setTimeout(function(){
					$("#sw-search-input").focus();
				}, 500);
			} else {
				$("#sw-mystart-search").attr("aria-hidden", "true");
				$(".header-icon.search").focus();
			}





		});


		// ********** HEADER ********** //
		// ADD LOGO
		var logoCheck = "true";
		var logoSrc = jQuery.trim("/cms/lib/NJ01912870/Centricity/Template/GlobalAssets/images///logos/pemberton-logo.png");
		if(logoCheck == "true") {
			var srcSplit = logoSrc.split("/");
			var srcSplitLen = srcSplit.length;
			if((logoSrc != "") && (srcSplit[srcSplitLen - 1] != "default-man.jpg")) {
				$("#gb-logo").append('').removeClass("hidden");
			} else {
				$("#gb-logo").append('').removeClass("hidden");
			}
		}

		// ADD SITENAME
		var siteNameOne = jQuery.trim('Pemberton Township Schools');
		var siteNameTwo = jQuery.trim('Burlington County, New Jersey');
		if((siteNameOne == "") && (siteNameTwo == "")) {
			var splitLen = 2;
			var siteName = "Pemberton Township Schools";
			siteName = siteName.split(" ");
			var siteNameLength = siteName.length;
			if(siteNameLength > 2){
				siteNameEnd = jQuery.trim(siteName.splice(-splitLen, siteName.length).toString().replace(/,/g, " "));
			} else {
				siteNameEnd = jQuery.trim(siteName.splice(-1, siteName.length).toString().replace(/,/g, " "));
			}
			siteNameBegin = jQuery.trim(siteName.toString().replace(/,/g, " "));
			$("#gb-sitename").prepend("
" + siteNameBegin + "
" + siteNameEnd + "");
		} else if((siteNameOne != "") && (siteNameTwo == "")) {
			$("#gb-sitename").prepend("
" + siteNameOne + "");
		} else if((siteNameOne == "") && (siteNameTwo != "")) {
			$("#gb-sitename").prepend("
" + siteNameTwo + "");
		} else if((siteNameOne != "") && (siteNameTwo != "")) {
			$("#gb-sitename").prepend("
" + siteNameOne + "
" + siteNameTwo + "");
		}

		// ********** CHANNEL BAR ********** //
		$(".sw-channel-item").unbind('hover');
		$(".sw-channel-item").hover(function(){
			$(".sw-channel-item ul").stop(true, true);
			var subList = $(this).children('ul');
			if ($.trim(subList.html()) !== "") {
				subList.slideDown(300, "swing");
			}
			$(this).addClass("hover");
		}, function(){
			$(".sw-channel-dropdown").slideUp(300, "swing");
			$(this).removeClass("hover");
		});

		// ********** BODY ********** //
		// AUTO FOCUS SIGN IN FIELD
		$("#swsignin-txt-username").focus();

		//PLACE THE HEADLINES READ MORE BUTTON THE THE APP FOOTER
		$(".headlines").each(function(){
			//$("a.more-link", this).prependTo(".ui-widget-footer", this).show();
			var moreLink = $(this).find("a.more-link");
			$(".ui-widget-footer", this).prepend(moreLink).addClass("show-more");
		});

		//VIEW CALENDAR LINK TEXT
		$(".view-calendar-link span").text("Full Calendar").show();

		//CENTER SIGNED OUT MESSAGE AND SIGNIN BUTTON
        	if($("div.ui-spn > div > p > span").text() == "You have been signed out."){
        		$("div.ui-spn > div").css({"text-align" : "center", "padding" : "50px 0px 50px 0px"});
        		//ADD SIGNED OUT BREADCRUMB
        		$(".ui-breadcrumbs").append("
Signed Out");
        	}

        	//ADD SIGN IN BREADCRUMB
        	if($("#swlogin").length){
        		$("#sw-content-layout-wrapper .ui-widget.app").addClass("signin");
        		$(".ui-breadcrumbs").append("
Sign In");
        	}

        	//REGISTER BREADCRUMB
		if($("#swageprompt-txt-birthmonth").length && $("#swageprompt-txt-birthday").length && $("#swageprompt-txt-birthyear").length) {
			$("#sw-content-layout-wrapper .ui-widget.app").addClass("register");
			$(".ui-breadcrumbs").append("
Register");
		}

		//SEARCH RESULTS BREADCRUMB
		if($("#sw-content-layout-wrapper.ui-spn #swsearch-pnl-main").length) {
			$("#sw-content-layout-wrapper").children().wrapAll('

');
			$(".ui-breadcrumbs").append("
Search Results");
		}


		// CENTER SIGNED OUT MESSAGE AND SIGNIN BUTTON
        if($("div.ui-spn > div > p > span").text() == "You have been signed out."){
            $("div.ui-spn > div").css({"text-align" : "center", "padding" : "50px 0px 50px 0px"});
            //DOC add signed out breadcrumb
            $(".ui-breadcrumbs").append("
Signed Out");
        }

        // ADJUST SIGN IN PAGE
        if($("#swlogin").length){
            $("#sw-content-layout-wrapper .ui-widget.app").addClass("signin");
            $(".ui-breadcrumbs").append("
Sign In");
        }

        // ADJUST REGISTER PAGE
		if($("#swageprompt-txt-birthmonth").length && $("#swageprompt-txt-birthday").length && $("#swageprompt-txt-birthyear").length) {
			$("#sw-content-layout-wrapper .ui-widget.app").addClass("register");
			$(".ui-breadcrumbs").append("
Register");
		}

		// ADJUST SEARCH RESULTS PAGE
		if($("#sw-content-layout-wrapper.ui-spn #swsearch-pnl-main").length) {
			$("#sw-content-layout-wrapper").children().wrapAll('

');
			$(".ui-breadcrumbs").append("
Search Results");
		}

        // ADD READ FULL STORY BUTTON TO EACH HEADLINE
        $(".ui-widget.app.headlines").each(function(){
            $(".ui-article", this).each(function(){
                var headlineURL = $("h1 a", this).attr("href");
                $(".ui-article-description", this).append('Read Full Story »');
            });
        });

        // APPLY RESPONSIVE DIMENSIONS TO CONTENT IMAGES
        $("div.ui-widget.app .ui-widget-detail img")
            .not($("div.ui-widget.app.cs-rs-multimedia-rotator .ui-widget-detail img"))
            .not($("div.ui-widget.app.gallery.json .ui-widget-detail img"))
            .each(function() {
                if ($(this).attr('width') !== undefined && $(this).attr('height') !== undefined) { // IMAGE HAS INLINE DIMENSIONS
                    $(this).css({"display": "inline-block", "width": "100%", "max-width": $(this).attr("width") + "px", "height": "auto", "max-height": $(this).attr("height") + "px"});
                }
        });

        // MAKE NAVIGATION APPS ACCESSIBLE
        head.ready("accessibleNavApp", function(){
            $(".ui-widget.app.navigation").csAccessibleNavigationApp();
        });


		// ********** FOOTER ********** //
		// MOVE SCHOOLWIRES FOOTER STUFF
		$(".gb-schoolwires-footer.logo").html($("#sw-footer-logo").html());
		$(".gb-schoolwires-footer.links").append($("#sw-footer-links li:eq(0) a"));
		$(".gb-schoolwires-footer.links").append($("#sw-footer-links li:eq(2) a"));
		$(".gb-schoolwires-footer.links").append($("#sw-footer-links li:eq(1) a"));
		$(".gb-schoolwires-footer.links").append($("#sw-footer-copyright"));	

		// FOOTER LINKS
		$(".footer-link.to-top").click(function(e){
			e.preventDefault();
            		$("html, body").animate({ scrollTop: 0 }, "slow", function() {
            			$("li.sw-channel-item:first-child > a").focus();
            		});
		});
	}

    function rsMenu() {
    	head.ready("rsMainMenu", function(){
            // RESPONSIVE MAIN MENU
            $("body").csRsMenu({
                "breakPoint"            : 768, // SYSTEM BREAK POINTS - 768, 640, 480, 320
                "slideDirection"        : "left-to-right", // OPTIONS - left-to-right, right-to-left   
                "primaryColor"          : "#1A1A1A", // DEFAULT - #1A1A1A
                "secondaryColor"        : "#0C0C0C", // DEFAULT - #0C0C0C
                "accentColor"           : "#BABABA", // DEFAULT - #BABABA
                "menuButtonParent"      : "#gb-header",
                "showMenuBtnText"       : "yes",
                "menuBtnText"           : "Menu",
                "showSchools"           : "yes",
                "showTranslate"         : "yes",
                "translateId"           : "none",
                "showAccount"           : "yes",
                "extraMenuOptions"      : "Quick Links", // EXTRA MENU HEADING NAMES SEPARATED BY A ;
