function resizeSlider_ir3784() {

	var windowWidth = parseFloat(document.body.offsetWidth);
	var containerWidth = windowWidth;

	var contentWidth = parseFloat("1038");
	if(contentWidth==0) {
		contentWidth = containerWidth;
	}

	if(windowWidth<contentWidth) {
		containerWidth = contentWidth;
	}

	document.getElementById("ir3784-ir3784-slider").style.width = containerWidth + "px";
	var fadeSlice = document.getElementById("nivo-slice-0");
	if(fadeSlice!=null) {
		fadeSlice.style.width = containerWidth + "px";
	}
	// document.getElementById("ir3784-ir3784-slider").style.height = Math.max((parseFloat("561")/parseFloat("1038") * containerWidth), parseFloat("561")) + "px";
	document.getElementById("ir3784-ir3784-slider").style.marginLeft = ((contentWidth - containerWidth) / 2.0) + "px";

}

