        "StreamingVideo": function() {
        	// FOR SCOPE
            var _this = this;

        	if($(".hp").length) {
            	$("#hp-video").css("height", "calc(100vh - " + $("header").height() + "px)");

                $.ajax({
                    url : "https://noembed.com/embed?callback=?",
                    dataType: "jsonp",
                    contentType: "application/json; charset=utf-8",
                    async: false,
                    data : { "url": "https://www.youtube.com/watch?v=gnkXPW5mGx0" },
                    success: function(data) {
                        _this.StreamingVideoWidth = data.width;
                        _this.StreamingVideoHeight = data.height;

                        //loads the IFrame Player API code asynchronously.
                        var youTubeTag = document.createElement('script');
                        youTubeTag.src = "https://www.youtube.com/iframe_api";
                        var firstYouTubeScriptTag = document.getElementsByTagName('script')[0];
                        firstYouTubeScriptTag.parentNode.insertBefore(youTubeTag, firstYouTubeScriptTag);
                    },
                    error: function(data) {
                    	// JUST IN CASE THE noembed SERVICE EVER FAILS, WE'RE STILL GOING TO RUN THE STREAMING VIDEO SCRIPT AND USE THE DEFAULT WIDTH AND HEIGHT DIMENSIONS AS SET ABOVE

                        //loads the IFrame Player API code asynchronously.
                        var youTubeTag = document.createElement('script');
                        youTubeTag.src = "https://www.youtube.com/iframe_api";
                        var firstYouTubeScriptTag = document.getElementsByTagName('script')[0];
                        firstYouTubeScriptTag.parentNode.insertBefore(youTubeTag, firstYouTubeScriptTag);
                    }
                });

                if(true) {
                	$('' +
                    	'
' +
                        	'
COVID-19 (Coronavirus), April 7 Update' +
                            '
We hope everyone is doing well and holding up under the circumstances. Thank you for another successful week of remote learning and for the enthusiastic response to our Virtual Learning Spirit Week!' +
                            '
[Continue]' +
                        '' +
                    '').insertBefore("#hp-video-viewer");
                }

                $(document).on("click keydown", "#hp-scroll-down", function(e) {
                    if(_this.AllyClick(e)) {
                        e.preventDefault();
                        $("html, body").animate({
                        	scrollTop: $(".hp-content-outer.one").offset().top
                        }, "slow");
                    }
                });
            }
        },

