        "Search": function() {
        	$("#cs-search-form").submit(function(e) {
            	e.preventDefault();
                if($.trim($("#cs-search-input").val()) != "" && $.trim($("#cs-search-input").val()) != "Search...") {
                	window.location.href = "/site/Default.aspx?PageType=6&SiteID=4&SearchString=" + $("#cs-search-input").val();
                }
            });
            $("#cs-search-input").focus(function() {
                if($(this).val() == "Search...") {
                    $(this).val("");
                }
            }).blur(function() {
                if($(this).val() == "") {
                    $(this).val("Search...");
                }
            });
        },

        "AllyClick": function(event) {
            if(event.type == "click") {
                return true;
            } else if(event.type == "keydown" && (event.keyCode == this.KeyCodes.space || event.keyCode == this.KeyCodes.enter)) {
                return true;
            } else {
                return false;
            }
        },

        "GetBreakPoint": function() {
            return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
        }
    };

    //creates an iframe (and YouTube player) after the API code downloads.
    var youTubePlayer;
    var windowWidth;
    var windowHeight;
    function onYouTubeIframeAPIReady() {
        youTubePlayer = new YT.Player('hp-video-container', {
            height: CreativeTemplate.StreamingVideoHeight,
            width: CreativeTemplate.StreamingVideoWidth,
            videoId: 'gnkXPW5mGx0',
            playerVars: { 
                'modestbranding': 1,
                'autoplay': 1,
                'controls': 0, 
                'rel' : 0,
                'loop': 1,
                'showinfo': 0,
                'wmode': 'opaque',
                // ADDED PLAYLIST PARAMETER BECAUSE AS NOTED IN THE DOCUMENTATION: To loop a single video, set the loop parameter value to 1 and set the playlist parameter value to the same video ID already specified in the Player API URL.
                // https://developers.google.com/youtube/player_parameters?playerVersion=HTML5
                'playlist': 'gnkXPW5mGx0'
            },
            events: {
                'onReady': onPlayerReady,
                'onStateChange': onPlayerStateChange
            }
        });
    }

    //the API will call this function when the video player is ready.
    function onPlayerReady(event) {
        // APPLY FULLSCREEN RESPONSIVE DIMENSIONS
        var dynStyleSheet = document.createElement('style');
		if(dynStyleSheet) {
            dynStyleSheet.setAttribute('type', 'text/css');
            var head = document.getElementsByTagName('head')[0];

            if(head) {
                head.insertBefore(dynStyleSheet, head.childNodes[0]);
            }

            var dynStyles =	'@media (min-width: 1024px) {' +
            					'#hp-video-container {' +
            						'min-width: calc(100vh / ' + (CreativeTemplate.StreamingVideoHeight / CreativeTemplate.StreamingVideoWidth) + ');' +
                                	'min-height: calc(100vw * ' + (CreativeTemplate.StreamingVideoHeight / CreativeTemplate.StreamingVideoWidth) + ');' +
            					'}' +
                            '}' +
                            '@media (max-width: 1023px) {' +
                            	'#hp-video-container {' +
            						'width: 100vw;' +
                                    'height: calc(100vw * ' + (CreativeTemplate.StreamingVideoHeight / CreativeTemplate.StreamingVideoWidth) + ');' +
                                '}' +
                            '}';

            var rules = document.createTextNode(dynStyles);

            if(dynStyleSheet.styleSheet){ // IE
                dynStyleSheet.styleSheet.cssText = dynStyles;
            } else {
                dynStyleSheet.appendChild(rules);
            }
        }

        $("#hp-video-container").attr("tabindex", "-1");

        youTubePlayer.mute();

        if($("html").hasClass("mobile")) {
        	$(".hp-video-control.play-pause").attr({
            	"data-video-is-playing": "false",
                "aria-label": "Play Background Video"
            });
            $(".hp-video-control.mute-unmute").attr({
            	"data-video-is-muted": "true",
                "aria-label": "Unmute Background Video"
            });
        }

        $(document).on("click keydown", ".hp-video-control.mute-unmute", function(e) {
        	if(CreativeTemplate.AllyClick(e)) {
            	e.preventDefault();

                if($(this).attr("data-video-is-muted") == "true") {
                	youTubePlayer.unMute();
                     $(".hp-video-control.mute-unmute").attr({
                        "data-video-is-muted": "false",
                        "aria-label": "Mute Background Video"
                    });
                } else {
                	youTubePlayer.mute();
                    $(".hp-video-control.mute-unmute").attr({
                        "data-video-is-muted": "true",
                        "aria-label": "Unmute Background Video"
                    });
                }
            }
        });

        $(document).on("click keydown", ".hp-video-control.play-pause", function(e) {
        	if(CreativeTemplate.AllyClick(e)) {
            	e.preventDefault();

                if($(this).attr("data-video-is-playing") == "true") {
                	youTubePlayer.pauseVideo();
                    $(".hp-video-control.play-pause").attr({
                        "data-video-is-playing": "false",
                        "aria-label": "Play Background Video"
                    });
                } else {
                	youTubePlayer.playVideo();
                    $(".hp-video-control.play-pause").attr({
                        "data-video-is-playing": "true",
                        "aria-label": "Pause Background Video"
                    });
                }
            }
        });
    }

    //The API calls this function when the players state changes.
    //The function indicates that when playing a video (state=1),
    //the player should play for six seconds and then stop.
    function onPlayerStateChange(event) {}

