function UpdateUserControl(elem) {
    if (elem) {
        var userCtrl = document.getElementById(elem.getAttribute("userctrlid"));
        if (userCtrl) {
            userCtrl.style.display = elem.checked ? "" : "none";
        }
    }
}

	$(document).ready(function(){
    	$('.mainBanner').slick({
        	autoplay: true,
        	autoplaySpeed:  4500,
        	arrows: false,
        	dots: true,
        	fade: true
      	});

		$('.bannerStartStopControl').click( function(event) {
		       event.preventDefault();
		       if($(this).hasClass('pause')) {
		        $(this).removeClass('pause');
		        $(this).addClass('play');  
		        $(this).find('em').text('Play');
		        $('.mainBanner').slick('slickPause');
		       }
		       else {
		         $(this).removeClass('play');
		         $(this).addClass('pause');  
		         $(this).find('em').text('Pause');
		         $('.mainBanner').slick('slickPlay');
		       }
		    });
		    
		$('.bannerPrevControl').click(function(event){
			event.preventDefault();
			$('.mainBanner').slick('slickPrev');
		});
		$('.bannerNextControl').click(function(event){
			event.preventDefault();
			$('.mainBanner').slick('slickNext');
		});

		$("ul.slick-dots").appendTo("#mainSlider");
    });

