        window.resizeMiniCalendar20200420125507582 = function () {
        var calId = 'miniCal20200420125507582';
        var width = $('#' + calId).width();
        var calYearId = 'miniCalYear20200420125507582';
        var calMonthFullId = 'miniCalMonthFull20200420125507582';
        var calMonthShortId = 'miniCalMonthShort20200420125507582';
        var calGoToCalFullId = 'miniCalGotToCalFull20200420125507582';
        var calGoToCalShortId = 'miniCalGoToCalShort20200420125507582';
        if(width < 250) {
            $('#' + calYearId).hide();
            $('#' + calGoToCalFullId).hide();
            $('#' + calGoToCalShortId).show();
        }
        else {
            $('#' + calYearId).show();
            $('#' + calGoToCalFullId).show();
            $('#' + calGoToCalShortId).hide();
        }
        if(width < 170) {
                $('#' + calMonthFullId).hide();
                $('#' + calMonthShortId).show();                                       
        }
        else {
                $('#' + calMonthFullId).show();
                $('#' + calMonthShortId).hide();                                       
        }
    }               
}

