section.script:
var popupTimeout = 10; 

popupTimeout = 2000;

document.addEventListener("DOMContentLoaded", function(){
	console.log('INIT POPUP MESSAGE!!');
	initPopupMsg();

	var btn = document.querySelector('.popupMsgCloseA');
	btn.addEventListener('click', function (event) {
		closePipupMsg();
	});
});


function initPopupMsg ()
{
	console.log('TIMEOUT - '+ popupTimeout);

	setTimeout(function(){
		var elem = document.querySelector('html');
		elem.classList.add('is-clipped');

		var elem2 = document.querySelector('.initial-popup');
		elem2.classList.add('is-active');
	}, popupTimeout);
}

function closePipupMsg ()
{
	var elem = document.querySelector('html');
	elem.classList.remove('is-clipped');

	var elem2 = document.querySelector('.initial-popup');
	elem2.classList.remove('is-active');
}
