	function mobileGlobalIcons(){
		$("#mobile-global").on('click keypress', function(event){
                if(globalClick(event) === true){
					//DONT LET THE PAGE JUMP ON KEYPRESS
                    event.preventDefault();

                    if($(this).hasClass("open")){
						$("#gb-icons-outer").animate({
							right: "-250"
						}, 1000, function() {
							$("#gb-icons-outer").hide();

						});
						$(this).removeClass("open");

                    } else {
						$("#gb-icons-outer").show().animate({
							right: "0"
						}, 1000, function() {

						});
						$(this).addClass("open");
                    }


                }
         });
	}

	function globalClick(event){
        if(event.type === 'click'){
            return true;
        }
        else if(event.type === 'keypress'){
            var code = event.charCode || event.keyCode;
            if((code === 32)|| (code === 13)){
                return true;
            }
        }
        else{
            return false;
        }
    }
