	function modMyStart(){
		//DISTRICT HOME BUTTON TEXT
		$(".sw-dropdown-arrow").text("");

        //BUILDING MY OWN SCHOOLLIST
        var acSchoolList = '';

        $(".sw-mystart-dropdown.schoollist li").each(function(){
        	var schoolItem = $(this).html();
            acSchoolList += "
"+schoolItem+"";
        });


        //MOVING ANY MYSTART ELEMENTS TO MATCH DESIGN
        $("#ac-school-list").append(acSchoolList);
		$("#sw-mystart-search").insertAfter("#gb-mystart");

		//CALL TRANSLATE FUNCTION
		addFramesetTranslate();
	}
