/* ------------------------------   DYNAMIC CONTENT  ------------------------------ */
/* ------------------------------   ____________________   ------------------------------ */      
	function dynamicContent() {

		// ********** MYSTART ********** //
		$(document).off("click", ".sw-mystart-dropdown");
		$(document).on("click", ".sw-mystart-dropdown, .ac-dropdown-container", function(e) {
			$(".sw-dropdown, .ac-dropdown", this).attr("aria-hidden","false").slideDown(300, "swing", function(){

				setTimeout(function(){
					$(".sw-dropdown, .ac-dropdown", this).focus();
				}, 100);
			});
			$(this).addClass("open");

		});

		$(".sw-mystart-dropdown, .ac-dropdown-container").hover(function(e) {
			e.preventDefault();
		}, function() {

			$(".sw-dropdown, .ac-dropdown",this).attr("aria-hidden", "true").slideUp(300, "swing"); 
			$(this).removeClass("open"); 
		});

		//SHOW USER OPTIONS IF THEY ARE LOGGED IN
		if($("#sw-mystart-account").length){
        	$(".ac-dropdown-container.user-options").show();
            $(".ac-mystart-button.user").hide();
            $("#ui-btn-mypasskey span").text("PASSKEYS");
            $("#sw-mystart-mypasskey").insertBefore(".ac-dropdown-container.user-options");
        } else {
        	 //HIDE REGISTER BUTTON IF HIDDEN IN SITE MANAGER
              if(!$("#ui-btn-register").length){
                  $(".ac-mystart-button.signin").show();
              } else {
              	$(".ac-mystart-button.user").show();
              }

        }


		// ********** HEADER ********** //

		// SEARCH
		$(".search-control").on('click keypress', function(event){
	        if(globalClick(event) === true){
				//DONT LET THE PAGE JUMP ON KEYPRESS
	            event.preventDefault();
	            if($(this).hasClass("open")){
	            	$("#sw-mystart-search").animate({
						width: "0"
					}, 1000, function() {
						$("#sw-mystart-search").hide();
					});
					$(this).removeClass("open");
	            } else {
		            $("#sw-mystart-search").show().animate({
						width: "186"
					}, 1000, function() {
						$("#sw-search-input").focus();

					});
					$(this).addClass("open");
	            }

    		}
    	});



		// ADD LOGO
		var logoCheck = "true";
		var logoSrc = jQuery.trim("/cms/lib/NJ01001815/Centricity/Template/GlobalAssets/images///logos/Ville Logo New Small.png");
		if(logoCheck == "true") {
			var srcSplit = logoSrc.split("/");
			var srcSplitLen = srcSplit.length;
			if((logoSrc != "") && (srcSplit[srcSplitLen - 1] != "default-man.jpg")) {
				$("#gb-logo").append('').removeClass("hidden");
			} else {
				$("#gb-logo").append('').removeClass("hidden");
			}
		}

		// ADD SITENAME



		var siteNameOne = jQuery.trim('');
		var siteNameTwo = jQuery.trim('');

		if((siteNameOne == "") && (siteNameTwo == "")) {
			var splitLen = 2;
			var siteName = "Somerville Public School District";
			siteName = siteName.split(" ");
			var siteNameLength = siteName.length;
			if(siteNameLength > 2){
				siteNameEnd = jQuery.trim(siteName.splice(-splitLen, siteName.length).toString().replace(/,/g, " "));
			} else {
				siteNameEnd = jQuery.trim(siteName.splice(-1, siteName.length).toString().replace(/,/g, " "));
			}
			siteNameBegin = jQuery.trim(siteName.toString().replace(/,/g, " "));
			$("#gb-sitename").prepend("
" + siteNameBegin + "
" + siteNameEnd + "");
		} else if((siteNameOne != "") && (siteNameTwo == "")) {
			$("#gb-sitename").prepend("
" + siteNameOne + "");
		} else if((siteNameOne == "") && (siteNameTwo != "")) {
			$("#gb-sitename").prepend("
" + siteNameTwo + "");
		} else if((siteNameOne != "") && (siteNameTwo != "")) {
			$("#gb-sitename").prepend("
" + siteNameOne + "
" + siteNameTwo + "");
		}

		// ********** CHANNEL BAR ********** //
		$(".sw-channel-item").unbind('hover');
		$(".sw-channel-item").hover(function(){
			$(".sw-channel-item ul").stop(true, true);
			var subList = $(this).children('ul');
			if ($.trim(subList.html()) !== "") {
				subList.slideDown(300, "swing");
			}
			$(this).addClass("hover");
		}, function(){
			$(".sw-channel-dropdown").slideUp(300, "swing");
			$(this).removeClass("hover");
		});

		// ********** BODY ********** //

		// HANDLE LINKS IN APP HEADERS SO THEY WILL NOT INTERFERE WITH MOBILE ACCORDION
		$("#hp-content .ui-widget-header h1 a").on('click keypress', function(event){
                alert("clicked");
                if(globalClick(event) === true){
                	alert("clicked");
					//DONT LET THE PAGE JUMP ON KEYPRESS
                    event.preventDefault();
                 }
        });


		// AUTO FOCUS SIGN IN FIELD
		$("#swsignin-txt-username").focus();

		//PLACE THE HEADLINES READ MORE BUTTON INTO THE APP HEADER, AND ADJUST RSS BUTTON
		$(".headlines").each(function(){
			//$("a.more-link", this).prependTo(".ui-widget-footer", this).show();
			var moreLinkURL = $(this).find("a.more-link").attr("href");
			$(this).find(".ui-widget-header h1").append("view all posts");
			$(this).find(".ui-widget-detail").prepend("view all posts");
			$(this).find(".rss span").text("subscribe to our feed");
		});

		//PLACE THE UPCOMING EVENTS VIEW ALL BUTTON INTO THE APP HEADER
		$(".upcomingevents").each(function(){
			var viewEventsURL = $(this).find("a.view-calendar-link").attr("href");
			$(this).find(".ui-widget-header h1").append("view all events");
			$(this).find(".ui-widget-detail").prepend("view all events");
		});

		//CENTER SIGNED OUT MESSAGE AND SIGNIN BUTTON
        	if($("div.ui-spn > div > p > span").text() == "You have been signed out."){
        		$("div.ui-spn > div").css({"text-align" : "center", "padding" : "50px 0px 50px 0px"});
        		//ADD SIGNED OUT BREADCRUMB
        		$(".ui-breadcrumbs").append("
Signed Out");
        	}

        	//ADD SIGN IN BREADCRUMB
        	if($("#swlogin").length){
        		$("#sw-content-layout-wrapper .ui-widget.app").addClass("signin");
        		$(".ui-breadcrumbs").append("
Sign In");
        	}

        	//REGISTER BREADCRUMB
		if($("#swageprompt-txt-birthmonth").length && $("#swageprompt-txt-birthday").length && $("#swageprompt-txt-birthyear").length) {
			$("#sw-content-layout-wrapper .ui-widget.app").addClass("register");
			$(".ui-breadcrumbs").append("
Register");
		}

        //ACCOUNT PAGE BREADCRUMB
        if($("#myaccountedit-pnl-main").length){
        	$('
Account Settings').appendTo(".ui-breadcrumbs");
        }

		//SEARCH RESULTS BREADCRUMB
		if($("#sw-content-layout-wrapper.ui-spn #swsearch-pnl-main").length) {
			$("#sw-content-layout-wrapper").children().wrapAll('

');
			$(".ui-breadcrumbs").append("
Search Results");
		}


	        // ADD READ FULL STORY BUTTON TO EACH HEADLINE
	        $(".ui-widget.app.headlines").each(function(){
	            $(".ui-article", this).each(function(){
	                var headlineURL = $("h1 a", this).attr("href");
	                $(".ui-article-description", this).after('Discover More');
	            });
	        });

	        // APPLY RESPONSIVE DIMENSIONS TO CONTENT IMAGES
	        $("div.ui-widget.app .ui-widget-detail img")
	            .not($("div.ui-widget.app.cs-rs-multimedia-rotator .ui-widget-detail img"))
	            .not($("div.ui-widget.app.gallery.json .ui-widget-detail img"))
	            .each(function() {
	                if ($(this).attr('width') !== undefined && $(this).attr('height') !== undefined) { // IMAGE HAS INLINE DIMENSIONS
	                    $(this).css({"display": "inline-block", "width": "100%", "max-width": $(this).attr("width") + "px", "height": "auto", "max-height": $(this).attr("height") + "px"});
	                }
	        });

	        // MAKE NAVIGATION APPS ACCESSIBLE
	        head.ready("accessibleNavApp", function(){
	            $(".ui-widget.app.navigation").csAccessibleNavigationApp();
	        });


		// ********** FOOTER ********** //
		// MOVE SCHOOLWIRES FOOTER STUFF
		$(".gb-schoolwires-footer.logo").html($("#sw-footer-logo").html());
		$(".gb-schoolwires-footer.links").append($("#sw-footer-links li:eq(0) a"));
		$(".gb-schoolwires-footer.links").append($("#sw-footer-links li:eq(2) a"));
		$(".gb-schoolwires-footer.links").append($("#sw-footer-links li:eq(1) a"));
		$(".gb-schoolwires-footer.links").append($("#sw-footer-copyright"));	

		// FOOTER LINKS
		$(".to-top").click(function(e){
			e.preventDefault();
            		$("html, body").animate({ scrollTop: 0 }, "slow", function() {
            			$("li.sw-channel-item:first-child > a").focus();
            		});
		});
	}  
