/* ------------------------------   MULTIMEDIA ROTATOR  ------------------------------ */
/* ------------------------------   ____________________   ------------------------------ */
	function mmRotator(){
		head.ready("multiMediaRotator", function(){
			if(!$("#sw-content-container10.region.ui-hp .rs-gallery-container").length){
		        // MULTIMEDIA ROTATOR
		        $("#sw-content-container10.ui-hp div.ui-widget.app.cs-rs-multimedia-rotator").csRsMultimediaRotator({
		                "imageWidth"              : 730,
		                "imageHeight"             : 370,
		                "efficientLoad"           : "yes",
		                "appHeader"               : "no",
		                "titles"                  : "yes",
		                "captions"                : "yes",
		                "galleryOverlay"          : "no",
		                "linkedElement"           : [""], // ["image", "title", "overlay", "all"]
		                "playPause"               : "no",
		                "mainControls"            : "yes",
		                "contMoreLink"            : "no",
		                "contMoreLinkText"        : "More",
		                "descMoreLink"            : "no",
		                "descMoreLinkText"        : "Read More",
		                "bullets"                 : "no",
		                "bulletControls"          : "no",
		                "thumbnails"              : "no",
		                "thumbnailWidth"          : 122,
		                "thumbnailHeight"         : 53,
		                "thumbnailSlideDirection" : "horizontal", // horizontal or vertical
		                "hoverPause"              : "yes",
		                "autoRotate"              : "yes",
		                "fullScreenRotator"       : "no", // image dimensions work well at 1500x915
		                "fullScreenBreakpoints"   : ["768"], // OPTIONS ARE ["960", "768", "640", "480", "320"]
		                "fullScreenVideo"         : "no",
		                "transitionType"          : "slide", // fade or slide
		                "transitionSpeed"         : 3,
		                "transitionDelay"         : 9,
		                "customResize" : "yes",
						"customResizeFunction" : function(element, imageWidth, imageHeight) {
							var screenWidth = $(window).width();
							if(screenWidth <= 768){
								imageWidth = 768;
								imageHeight = 389;								
							}

							// RESET THE WIDTHS, HEIGHTS, AND SLIDE POSITION MAINLY IN CASE THE USER IS DRAGGING THE CORNER OF THEIR BROWSER
							// UPDATE WIDTH AND HEIGHT VARIABLES
							// SET viewWidth VARIABLE WITH A MAX-WIDTH OF config.imageWidth
							var viewWidth = ($(element).width() > imageWidth) ? imageWidth : $(element).width();
							var viewRatio = imageHeight / imageWidth;
							var viewHeight = viewWidth * viewRatio;
							var slideNum = $(".rs-gallery-images-list li", element).length;
							var sliderWidth = viewWidth * slideNum;
							if($(".rs-gallery-videos").length) {
								var vidWidth = $(".rs-gallery-videos .video", element).width();
								var vidHeight = $(".rs-gallery-videos .video", element).height() - 27;
							}

							// UPDATE LEFT POSITION OF VIEWER SLIDE
							var slidePos = $(".rs-gallery-images-list li.active", element).index();
							viewWidth = parseInt(viewWidth);
							var newPos = slidePos * viewWidth;

							// SET THE CSS PROPERTIES
							$(".rs-gallery-container", element).css({"height": viewHeight + "px"});
							$(".rs-gallery-container .rs-gallery-images", element).css({"height": viewHeight + "px"});
							$(".rs-gallery-container .rs-gallery-images .rs-gallery-images-list", element).css({"width": sliderWidth + "px", "height": viewHeight + "px", "left": "-" + newPos + "px"});
							$(".rs-gallery-container .rs-gallery-images .rs-gallery-images-list li", element).css({"width": viewWidth + "px", "height": viewHeight + "px"});
							$(".rs-gallery-container .rs-gallery-images .rs-gallery-images-list li img", element).removeAttr("style");

						},
		                "onImageLoad"             : function() { },
		                "allImagesLoaded"         : function() { },
		                "onThumbnailLoad"         : function() { },
		                "allThumbnailsLoaded"     : function() { },
		                "onTransitionStart"       : function() { },
		                "onTransitionEnd"         : function() { },
		                "watchVidClick"           : function() { },
		                "closeVidClick"           : function() { },
		                "allLoaded"               : function() { centerSlideshow(); }
		        });
			}
		});
	}

	function centerSlideshow(){
		var screenWidth = $(window).width();

		//ADD ELEMENTS FOR PHOTO OVERLAYS (CANT USE PSEUDO SINCE THEY NEED TO BE AFFECTED BY JS)
		if(!$(".rs-gallery-container .photo-divider").length){
			$(".rs-gallery-container").prepend("
").append("
");
		}

		if(screenWidth > 768){
			var imageWidth = 730;
			var slideShowOffset = ($(window).width() - imageWidth) / 2;
			$("#hp-slideshow .rs-gallery-images").css("margin-left",slideShowOffset+"px");
			$(".photo-divider.left").css("left",slideShowOffset-2+"px");
			$(".photo-divider.right").css("right",slideShowOffset-2+"px");
		} else {
			$("#hp-slideshow .rs-gallery-images").css("margin-left","0px");
		}



	}
