/* ------------------------------   ACCESSIBILITY  ------------------------------ */
/* ------------------------------   ____________________   ------------------------------ */
	function accessibilityFunctions(){        


		$("#sw-mystart-mypasskey").attr({
			"aria-label":	"Open My Passkey",
			"role":		"button"
		});

		// KEY MAPPING
		var csKeyCodes = { "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40, "tab": 9 };


		$(".ac-dropdown-container.translate .selector").keydown(function(e) {
			// CAPTURE KEY CODE
			switch(e.keyCode) {
				// CONSUME LEFT AND UP ARROWS
				case csKeyCodes.enter:
				case csKeyCodes.space:
					e.preventDefault();

					//IF THE DROPDOWN IS OPEN, CLOSE IT
					if($(".ac-dropdown-container.translate").hasClass("open")){
						$(".ac-dropdown-container.translate .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
						$(".ac-dropdown-container.translate").removeClass("open"); 
					} else {
						$(".ac-dropdown-container.translate .ac-dropdown").attr("aria-hidden","false").slideDown(300, "swing", function(){

							setTimeout(function(){
								$(".ac-dropdown-container.translate li:first-child > a").focus();
							}, 100);
						});
						$(".ac-dropdown-container.translate").addClass("open");
					}



				break;

				// CONSUME TAB KEY
				case csKeyCodes.tab:
					if(e.shiftKey){
						$(".ac-dropdown-container.translate .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
						$(".ac-dropdown-container.translate").removeClass("open");
					} else {
						e.preventDefault();
						$(".ac-dropdown-container.translate .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
						$(".ac-dropdown-container.translate").removeClass("open");
						$(".ac-dropdown-container.school-list .selector").focus();
					}

				break;

				// CONSUME LEFT AND UP ARROWS
				case csKeyCodes.down:
				case csKeyCodes.right:
					e.preventDefault();

					$(".ac-dropdown-container.translate .ac-dropdown-list li:first-child > a").focus();

				break;
			}
		});

		$(".ac-dropdown-container.translate li a").keydown(function(e) {
			// CAPTURE KEY CODE
			switch(e.keyCode) {
				// CONSUME LEFT AND UP ARROWS
				case csKeyCodes.left:
				case csKeyCodes.up:
					e.preventDefault();

					// IS FIRST ITEM
					if($(this).parent().is(":first-child")) {
						// FOCUS DROPDOWN BUTTON
						$(".ac-dropdown-container.translate .selector").focus();
					} else {
						// FOCUS PREVIOUS ITEM
						$(this).parent().prev("li").find("> a").focus();
					}
				break;

				// CONSUME RIGHT AND DOWN ARROWS
				case csKeyCodes.right:
				case csKeyCodes.down:
					e.preventDefault();

					// IS LAST ITEM
					if($(this).parent().is(":last-child")) {
						// FOCUS FIRST ITEM
						$(".ac-dropdown-container.translate .ac-dropdown-list li:first-child > a").focus();
					} else {
						// FOCUS NEXT ITEM

						$(this).parent().next("li").find("> a").focus();
					}
				break;

				// CONSUME HOME KEY
				case csKeyCodes.home:
					e.preventDefault();

					// FOCUS FIRST ITEM
					$(".ac-dropdown-container.translate .ac-dropdown-list li:first-child > a").focus();
				break;

				// CONSUME END KEY
				case csKeyCodes.end:
					e.preventDefault();

					// FOCUS LAST ITEM
					$(".ac-dropdown-container.translate .ac-dropdown-list li:last-child > a").focus();
				break;

			}
		});




        $(".ac-dropdown-container.school-list .selector").keydown(function(e) {
			// CAPTURE KEY CODE
			switch(e.keyCode) {
				// CONSUME LEFT AND UP ARROWS
				case csKeyCodes.enter:
				case csKeyCodes.space:
					e.preventDefault();

					//IF THE DROPDOWN IS OPEN, CLOSE IT
					if($(".ac-dropdown-container.school-list").hasClass("open")){
						$(".ac-dropdown-container.school-list .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
						$(".ac-dropdown-container.school-list").removeClass("open"); 
					} else {
						$(".ac-dropdown-container.school-list .ac-dropdown").attr("aria-hidden","false").slideDown(300, "swing", function(){

							setTimeout(function(){
								$(".ac-dropdown-container.school-list li:first-child > a").focus();
							}, 100);
						});
						$(".ac-dropdown-container.school-list").addClass("open");
					}



				break;

				// CONSUME TAB KEY
				case csKeyCodes.tab:
					if(e.shiftKey){
						$(".ac-dropdown-container.school-list .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
						$(".ac-dropdown-container.school-list").removeClass("open");
					} else {
						e.preventDefault();
						$(".ac-dropdown-container.school-list .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
						$(".ac-dropdown-container.school-list").removeClass("open");
                        if($("#sw-mystart-account").length){
                        	$("#ui-btn-mypasskey").focus();
                        } else {
                        	$(".ac-mystart-button.signin a").focus();
                        }

					}

				break;

				// CONSUME DOWN AND RIGHT ARROWS
				case csKeyCodes.down:
				case csKeyCodes.right:
					e.preventDefault();

					$(".ac-dropdown-container.school-list li:first a").focus();

				break;
			}
		});

		$(".ac-dropdown-container.school-list li a").keydown(function(e) {

            // CAPTURE KEY CODE
			switch(e.keyCode) {
				// CONSUME LEFT AND UP ARROWS
				case csKeyCodes.left:
				case csKeyCodes.up:
					e.preventDefault();

					// IS FIRST ITEM
					if($(this).parent().is(":first-child")) {
						// FOCUS DROPDOWN BUTTON
						$(".ac-dropdown-container.school-list .selector").focus();
					} else {
						// FOCUS PREVIOUS ITEM
						$(this).parent().prev("li").find("> a").focus();
					}
				break;

				// CONSUME RIGHT AND DOWN ARROWS
				case csKeyCodes.right:
				case csKeyCodes.down:
					e.preventDefault();

					// IS LAST ITEM
					if($(this).parent().is(":last-child")) {
						// FOCUS FIRST ITEM
						$(".ac-dropdown-container.school-list .ac-dropdown-list li:first-child > a").focus();
					} else {
						// FOCUS NEXT ITEM
						$(this).parent().next("li").find("> a").focus();
					}
				break;

				// CONSUME HOME KEY
				case csKeyCodes.home:
					e.preventDefault();

					// FOCUS FIRST ITEM
					$(".ac-dropdown-container.school-list .ac-dropdown-list li:first-child > a").focus();
				break;

				// CONSUME END KEY
				case csKeyCodes.end:
					e.preventDefault();

					// FOCUS LAST ITEM
					$(".ac-dropdown-container.school-list .ac-dropdown-list li:last-child > a").focus();
				break;

			}
		});






        $(".ac-dropdown-container.user-options .selector").keydown(function(e) {
			// CAPTURE KEY CODE
			switch(e.keyCode) {
				// CONSUME LEFT AND UP ARROWS
				case csKeyCodes.enter:
				case csKeyCodes.space:
					e.preventDefault();

					//IF THE DROPDOWN IS OPEN, CLOSE IT
					if($(".ac-dropdown-container.user-options").hasClass("open")){
						$(".ac-dropdown-container.user-options .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
						$(".ac-dropdown-container.user-options").removeClass("open"); 
					} else {
						$(".ac-dropdown-container.user-options .ac-dropdown").attr("aria-hidden","false").slideDown(300, "swing", function(){

							setTimeout(function(){
								$(".ac-dropdown-container.user-options li:first-child > a").focus();
							}, 100);
						});
						$(".ac-dropdown-container.user-options").addClass("open");
					}



				break;

				// CONSUME TAB KEY
				case csKeyCodes.tab:
					if(e.shiftKey){
						$(".ac-dropdown-container.user-options .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
						$(".ac-dropdown-container.user-options").removeClass("open");
					} else {
						e.preventDefault();
						$(".ac-dropdown-container.user-options .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
						$(".ac-dropdown-container.user-options").removeClass("open");
                        $(".search-control").focus();

					}

				break;

				// CONSUME DOWN AND RIGHT ARROWS
				case csKeyCodes.down:
				case csKeyCodes.right:
					e.preventDefault();

					$(".ac-dropdown-container.user-options li:first a").focus();

				break;
			}
		});

		$(".ac-dropdown-container.user-options li a").keydown(function(e) {

            // CAPTURE KEY CODE
			switch(e.keyCode) {
				// CONSUME LEFT AND UP ARROWS
				case csKeyCodes.left:
				case csKeyCodes.up:
					e.preventDefault();

					// IS FIRST ITEM
					if($(this).parent().is(":first-child")) {
						// FOCUS DROPDOWN BUTTON
						$(".ac-dropdown-container.user-options .selector").focus();
					} else {
						// FOCUS PREVIOUS ITEM
						$(this).parent().prev("li").find("> a").focus();
					}
				break;

				// CONSUME RIGHT AND DOWN ARROWS
				case csKeyCodes.right:
				case csKeyCodes.down:
					e.preventDefault();

					// IS LAST ITEM
					if($(this).parent().is(":last-child")) {
						// FOCUS FIRST ITEM
						$(".ac-dropdown-container.user-options .ac-dropdown-list li:first-child > a").focus();
					} else {
						// FOCUS NEXT ITEM
						$(this).parent().next("li").find("> a").focus();
					}
				break;

				// CONSUME HOME KEY
				case csKeyCodes.home:
					e.preventDefault();

					// FOCUS FIRST ITEM
					$(".ac-dropdown-container.user-options .ac-dropdown-list li:first-child > a").focus();
				break;

				// CONSUME END KEY
				case csKeyCodes.end:
					e.preventDefault();

					// FOCUS LAST ITEM
					$(".ac-dropdown-container.user-options .ac-dropdown-list li:last-child > a").focus();
				break;

			}
		});

	}
