    function getAnnouncementData(btnName) {

        var hdnCountVal = $("#dnn_ctr" + btnName.className + "_Announcements_hdnCount").val();
        $.ajax({
            type: "POST",
            url: "/desktopmodules/announcements/Announcements.asmx/GetData",
            data: "{'btnName':'" + btnName.text + "','ModuleID':'" + btnName.className + "','PortalID':'" + 1057 + "','UserID':'" + -1 + "','TabModuleId':'" + 163636 + "','TabId':'" + 68412 + "','hdnCount':'" + hdnCountVal + "'}",
            contentType: "application/json; charset=utf-8",
            dataType: "json",
            success: function (data) {

                $("#dnn_ctr" + btnName.className + "_Announcements_Announcements").html(data.d[1]);//to bind data
                eval(data.d[0]);//to apply css

            },
            failure: function (response) {
                alert(response.d);
            },
            error: function (response) {
                alert(response.d);
            }
        });

        return false;
    }

