div.script:
//Main Pictures
var MainPics = new Array();
MainPics[2] = '091318_3.jpg';
MainPics[3] = '091318_4.jpg';
MainPics[4] = '091318_5.jpg';
MainPics[5] = '091318_6.jpg';
MainPics[6] = '091318_7.jpg';
MainPics[7] = '091318_8.jpg';

//Current Picture
var CurrentPicture = 0;

function onBefore(curr, next, opts, fwd) {
// on Before arguments: 
// curr == DOM element for the slide that is currently being displayed 
// next == DOM element for the slide that is about to be displayed 
// opts == slideshow options 
// fwd  == true if cycling forward, false if cycling backward 

//Increment the current picture
CurrentPicture += 1;

// on the first pass, addSlide is undefined (plugin hasn't yet created the fn yet) 
if (!opts.addSlide)
    return;

// have we added all our slides? 
if (opts.slideCount == MainPics.length)
    return;

if (CurrentPicture > 1) {
    // add our next slide 
   opts.addSlide("", fwd == false);
}
}

$("#slidepicture").html('').cycle({
fx: "fade",
startingSlide: 0,
speed: 2000,
timeout: 7000,
before: onBefore
});

    function OnClientMouseOverHandler(sender, eventArgs) {
        if (eventArgs.get_item().get_parent() == sender) {
            sender.set_clicked(false);
        }
    }

    function OnClientLoad() {
        // adds attribute 'aria-label' to each anchor for OCR compliance (A: 2.4.4 Link Purpose (In Context))
        $(".RadMenu > .rmRootGroup > .rmItem").each(function () {
            var parentNodeTitle = $(this).children('a').attr('title');
            $(this).children('a').attr("aria-label", parentNodeTitle + " Dropdown Menu"); // set 'aria-label' for parent nodes
            
            var $this = $(this);
            $this.find('.rmSlide .rmItem > .rmLink').each(function () {
                $(this).attr("aria-label", parentNodeTitle + " Dropdown Menu" + $(this).attr('title') + " Menu Item"); // set 'aria-label' for child nodes
            });
        });
    }

