function resizeSlider_ir3736() {

	var windowWidth = parseFloat(document.body.offsetWidth);
	var containerWidth = windowWidth;

	var contentWidth = parseFloat("1000");
	if(contentWidth==0) {
		contentWidth = containerWidth;
	}

	if(windowWidth<contentWidth) {
		containerWidth = contentWidth;
	}

	document.getElementById("containerDIV-id-ir3736-slider").style.width = containerWidth + "px";
	var fadeSlice = document.getElementById("nivo-slice-0");
	if(fadeSlice!=null) {
		fadeSlice.style.width = containerWidth + "px";
	}
	// document.getElementById("containerDIV-id-ir3736-slider").style.height = Math.max((parseFloat("758")/parseFloat("1466") * containerWidth), parseFloat("758")) + "px";
	document.getElementById("containerDIV-id-ir3736-slider").style.marginLeft = ((contentWidth - containerWidth) / 2.0) + "px";

}

if(window.addEventListener) {
	window.addEventListener("resize", function() { resizeSlider_ir3736(); }, false);
} else if(window.attachEvent) {
	window.attachEvent("onresize", function() { resizeSlider_ir3736(); });
}
resizeSlider_ir3736();

function playerReady(thePlayer) {
	player = window.document[thePlayer.id];
	try{
		player.addModelListener("STATE", "window.videoInterface.onPlayerStateChange");
		player.addModelListener("ERROR", "window.videoInterface.onPlayerPlaybackError");
		player.addModelListener("TIME", "window.videoInterface.onPlayerTimeChange");
		player.addModelListener("META", "window.videoInterface.onMeta");
		player.addControllerListener("SEEK", "window.videoInterface.onPlayerSeek");
	} catch (exp) {
		if(window.console)
			console.log(exp);
	}
}

