	var contentWidth = parseFloat("1000");
	if(contentWidth==0) {
		contentWidth = containerWidth;
	}

	if(windowWidth<contentWidth) {
		containerWidth = contentWidth;
	}

	document.getElementById("ir3742-ir3742-slider").style.width = containerWidth + "px";
	var fadeSlice = document.getElementById("nivo-slice-0");
	if(fadeSlice!=null) {
		fadeSlice.style.width = containerWidth + "px";
	}
	// document.getElementById("ir3742-ir3742-slider").style.height = Math.max((parseFloat("200")/parseFloat("333") * containerWidth), parseFloat("200")) + "px";
	document.getElementById("ir3742-ir3742-slider").style.marginLeft = ((contentWidth - containerWidth) / 2.0) + "px";

}

imageRotatorJQuery = jQuery.noConflict();
imageRotatorJQuery(window).load(function() {
	var container = document.getElementById("ir3743");
	var rotator = document.getElementById("ir3743-ir3743-slider"); 
	if(container) {
		if(container.className!="ir3743-duplicateRotatorDiv") {
			var sliderNode = null;
			while(container.hasChildNodes()) {
				var lastChild = container.lastChild;
				if(lastChild.className=="ir3743-duplicateRotatorDiv") {
					sliderNode = lastChild.cloneNode(true);
					sliderNode.setAttribute("id", "ir3743-sliderContainer");
					sliderNode.style.display = "block";
				}
				container.removeChild(lastChild);
			}

			if(sliderNode) {
				container.appendChild(sliderNode);
			} else if(rotator) {
				container.appendChild(rotator);
			}
		}
		container.style.display = "block";
	}

	var _transition = 'random';
	if(/MSIE (\d+\.\d+);/.test(navigator.userAgent)) {
		var IEVersion = new Number(RegExp.$1);
		if(IEVersion<9) {
			_transition = 'fade';
		}
	}


    curNivo = imageRotatorJQuery('#ir3743-ir3743-slider').nivoSlider({
        transition: _transition,
        slices: Math.ceil("8"),
        boxCols: Math.ceil("5"),
        boxRows: Math.ceil("5"),
        animationSpeed: 500,
        rotateTime: 5000,
        showNavigation: false,
        beforeChange: function() { },
        afterChange: function() { },
        slideshowEnd: function() { },
        lastSlide: function() {

        },
        afterLoad: function() { },
        images: [

			{ 
				src: "//www.newheightsacademy.org/rotating_images/3743/3743_0_333_0_0.jpg",
				caption: {
					"text": "",
					"styling": "",
					"show": false
				},
				loaded: false
			},

			{ 
				src: "//www.newheightsacademy.org/rotating_images/3743/3743_1_333_0_0.jpg",
				caption: {
					"text": "",
					"styling": "",
					"show": false
				},
				loaded: false
			}

        ],
        preloadQueueLength: 2
    });
});

