link.script:

    $(function() {
        CreativeTemplate.Init();
        csGlobalJs.OpenInNewWindowWarning();
    });

    var CreativeTemplate = {
        // PROPERTIES
        "KeyCodes": { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 },
        "IsMyViewPage": false, // UPDATES IN SetTemplateProps METHOD
        "ShowDistrictHome": true,
        "ShowSchoolList": false, // UPDATES IN SetTemplateProps METHOD
        "ShowTranslate": true,

        // METHODS
        "Init": function() {
            // FOR SCOPE
            var _this = this;

            this.SetTemplateProps();
            this.Slideshow();
            this.CheckSlideshow();
            this.MyStart();
            this.Header();

            this.Shortcuts();
            this.GlobalIcons();
            this.SchoolList();
            this.Translate();       
            this.Search();
            this.RsMenu();
            this.ChannelBar();
            this.StickyChannelBar();
            this.StreamingVideo();
            this.ModEvents();
            this.Body();
            this.Homepage();
            this.AppAccordion();
            this.Footer();
            this.JsMediaQueries();

            $(window).load(function() { _this.WindowLoad(); });
            $(window).resize(function() { _this.WindowResize(); });
            $(window).scroll(function() { _this.WindowScroll(); });
        },

        "SetTemplateProps": function() {
            // MYVIEW PAGE CHECK
            if($("#pw-body").length) this.IsMyViewPage = true;

            // SCHOOL LIST CHECK
            if($(".sw-mystart-dropdown.schoollist").length) this.ShowSchoolList = true;
        },

        "WindowLoad": function() {
            this.HeadlinesImages();            
        },

        "WindowResize": function() {
            this.JsMediaQueries();
            this.Shortcuts();
            this.StickyChannelBar();

            //SET MARGIN TO BRING CHANNEL BAR ABOVE GALLERY
            if($("#gb-page.hp").length){
                var channelBarHeight = $("#gb-channel-list").outerHeight();
                $("#gb-channel-list").css("margin-top",-1*channelBarHeight);

                $("#hp-slideshow .mmg-description, #hp-slideshow-outer #hp-slideshow .cs-fullscreen-video-buttons, #hp-slideshow-outer #hp-slideshow .mmg-control-wrapper").css("bottom", channelBarHeight);
            }
        },

        "WindowScroll": function() {
            this.CheckSlideshow();
        },

        "JsMediaQueries": function() {
            switch(this.GetBreakPoint()) {
                case "desktop": 
                    //SET SITENAME PADDING
                    var logoWidth = $("#gb-logo-wrapper .gb-logo").outerWidth();

                    if(logoWidth == 0 && true == true){
                    	$("[data-show-logo='true'] #gb-sitename-outer").css("margin-left","150px").addClass("initialized");
                    } else {
                    	$("[data-show-logo='true'] #gb-sitename-outer").css("margin-left",logoWidth).addClass("initialized");
                    }


                    //SUBPAGE CONTENT MARGIN
                    if($("#gb-page.sp").length){
                        $("#sp-content-outer").css("margin-top",$("#gb-header-inner").outerHeight());
                    }
                break;
                case "768":
                    //SET SITENAME PADDING
                    var logoWidth = $(".header-column.left .gb-logo").outerWidth();
                    $("[data-show-logo='true'] #gb-sitename-outer").css("margin-left",logoWidth).addClass("initialized");
                break;
                case "640":
                    //SET SITENAME PADDING
                    var logoWidth = $(".header-column.left .gb-logo").outerWidth();
                    $("[data-show-logo='true'] #gb-sitename-outer").css("margin-left",logoWidth).addClass("initialized");
                break;
                case "480":
                    //SET SITENAME PADDING
                    var logoWidth = $(".header-column.left .gb-logo").outerWidth();
                    $("[data-show-logo='true'] #gb-sitename-outer").css("margin-left",logoWidth).addClass("initialized");
                break;
                case "320":

                break;
            }
        },

        "MyStart": function() {
            // FOR SCOPE
            var _this = this;

            // BUILD USER OPTIONS DROPDOWN
            var userOptionsItems = "";

            if($("#sw-myaccount-list").length){

                // MYVIEW BUTTON
                if($(".sw-mystart-button.pw").length) {
                    userOptionsItems += '
MyView';
                }

                // SITE MANAGER BUTTON
                if($(".sw-mystart-button.manage").length) {
                    $(".sw-mystart-button.manage a").attr("onclick", $(".sw-mystart-button.manage a").attr("onclick") + "return false;");
                    userOptionsItems += '
Site Manager';
                }

                // MY ACCOUNT BUTTONS
                $("#sw-myaccount-list > li:first-child a span").text("My Account");
                userOptionsItems += "
" + $("#sw-myaccount-list > li:first-child").html() + "";

                // MY PASSKEYS BUTTON
                if($("#sw-mystart-mypasskey").length) {
                    $("#sw-mystart-mypasskey a span").text("Passkeys");
                    $("#sw-mystart-mypasskey").addClass("cs-mystart-dropdown").removeClass("sw-mystart-button").insertBefore(".cs-mystart-link.signout");
                }

                //CHECK FOR DASHBOARD
                if($("#dashboard-sidebar").length){
                    $("#gb-header-top > .gb-section").css("padding-left","57px");
                }

                // ADD USER OPTIONS DROPDOWN TO THE DOM
                $(".cs-mystart-dropdown.user-options .cs-dropdown-list").html(userOptionsItems);
                $(".cs-mystart-dropdown.user-options, .cs-mystart-link.signout").show();

           } else {
                if(!$("#ui-btn-register").length){
                      $(".cs-mystart-link.guest.signin").show();
                  } else {
                    $(".cs-mystart-link.guest").show();
                  }
           }



            // BIND DROPDOWN EVENTS
            this.DropdownActions({
                "dropdownParent": ".cs-mystart-dropdown.user-options",
                "dropdownSelector": ".cs-selector",
                "dropdown": ".cs-dropdown",
                "dropdownList": ".cs-dropdown-list"
            });
        },

        "SchoolList": function() {
            // ADD SCHOOL LIST
