        "MMGPlugin": function() {
            // FOR SCOPE
            var _this = this;

            var mmg = eval("multimediaGallery" + $("#sw-content-container10 .ui-widget.app.multimedia-gallery:first").attr("data-pmi"));
            mmg.props.defaultGallery = false;

            $("#sw-content-container10 .ui-widget.app.multimedia-gallery:first").csMultimediaGallery({
                "efficientLoad" : true,
                "imageWidth" : 1500,
                "imageHeight" : 830,
                "mobileDescriptionContainer": [480, 320], // [960, 768, 640, 480, 320]
                "galleryOverlay" : false,
                "linkedElement" : [],  // ["image", "title", "overlay"]
                "playPauseControl" : true,
                "backNextControls" : true,
                "bullets" : false,
                "thumbnails" : false,
                "thumbnailViewerNum": [3, 4, 3, 3, 2], // NUMERICAL - [960 view, 768 view, 640 view, 480 view, 320 view]
                "autoRotate" : true,
                "hoverPause" : true,
                "transitionType" : "fade",
                "transitionSpeed" : 2,
                "transitionDelay" : 6,
                "fullScreenRotator" : true,
                "fullScreenBreakpoints" : [960], // NUMERICAL - [960, 768, 640, 480, 320]
                "onImageLoad" : function(props) {}, // props.element, props.recordIndex, props.mmgRecords
                "allImagesLoaded" : function(props) {}, // props.element, props.mmgRecords
                "onTransitionStart" : function(props) {

                },
                "onTransitionEnd" : function(props) {}, // props.element, props.currentRecordIndex, props.currentGalleryIndex, props.mmgRecords
                "allLoaded" : function(props) {
                    //WRAP CONTROLS
                    $(".mmg-control").wrapAll("
");
                    $(".mmg-control.play-pause").insertAfter(".mmg-control.back");

                }, // props.element, props.mmgRecords
                "onWindowResize": function(props) {

                } // props.element, props.mmgRecords
            });
        },

        "CheckSlideshow": function() {
            // FOR SCOPE
            var _this = this;

            if($(".hp").length && this.GetBreakPoint() != "desktop" && "Multimedia Gallery" != "Streaming Video") {
                if(this.GetBreakPoint() == "480" || this.GetBreakPoint() == "320"){


                      if ($(window).scrollTop() <= $("#hp-slideshow").offset().top + $("#hp-slideshow").height()) {
                        if(this.SlideshowDescFixed) {
                            $("#hp-slideshow").removeAttr("style");

                            this.SlideshowDescFixed = false;
                        }
                    } else {
                        if(!this.SlideshowDescFixed) {
                            $("#hp-slideshow").css({
                                "height": $("#hp-slideshow").height(),
                                "overflow": "hidden"
                            });

                            this.SlideshowDescFixed = true;
                        }
                    }
                }

            }
        },

        "StreamingVideo": function() {
