	$(window).load(function(){
		if($(window).width() >= 320 && $(window).width() <= 639){
			calendarListView();
		}
		imgResize();
		$(".icon-font").removeClass("hidden");
	});

	$(function(){
		// SET IE CLASSES TO BODY
		if(isMSIE){ $("body").addClass("msie"); }
		if(isMSIE8){ $("body").addClass("msie8"); }
		if(isMSIE9){ $("body").addClass("msie9"); }
		if(isMSIE10){ $("body").addClass("msie10"); }

		$(window).bind("resize", function(){ pgScreenSize(); imgResize(); sameHeightResize(); bulletResize(); });

		modEvents();
		$("#sw-content-container1.ui-hp").tabBlocks();
		tabCustomization();
		pgScreenSize();
		useChannelForSection();
		dynamicContent();
		searchText();
		sameHeights();		
	});	

	function pgScreenSize(){
		var getWidth = $(window).width();
		if (getWidth >= 1024) { // 960+
			photoGallery();
		} if (getWidth >= 768 && getWidth < 1024) { // 768+
			photoGallery();
		} if (getWidth >= 640 && getWidth < 768) { // 640+
			photoGallery();
			compileNav();
			responsiveSearch();
			appViewLarge();
		} if (getWidth >= 480 && getWidth < 640) { // 480+
			photoGallery();
			calendarListView();
			compileNav();
			responsiveSearch();
			appViewSmall();
		} if (getWidth >= 320 && getWidth < 480) { // 320+
			calendarListView();
			compileNav();
			responsiveSearch();
			appViewSmall();
		}
	}

	function photoGallery(){
		if(!$("#sw-content-container10.region.ui-hp .gallery-container").length){
			// PHOTO GALLERY CONFIGURATION
			$("#sw-content-container10.ui-hp div.ui-widget.app.gallery").creativeGalleryRotate({
				"imageHeight"		: 100,
				"imageControls"		: "off",
				"animateType"		: "fade", // CHOICES - "slide"
				"animateSpeed"		: 3,
				"pauseHover"		: ['all'], // CHOICES - ['title'], ['overlay'], ['caption'], ['all']
				"autoRotate"		: "on",
				"useTitles"		: "on",
				"useCaptions"		: "on",
				"useOverlay"		: "off",
				"randomOrder"		: "off",
				"linkedElement"		: ['overlay'], // CHOICES - ['picture'], ['title'], ['overlay']
				"itemizedBullets"	: "on",
				"thumbnails"		: "off",
				"thumbnailWidth"	: 50,
				"thumbnailHeight"	: 32,
				"thumbnailControls"	: "off",
				"thumbnailLimit"	: 3,
				"addClass"		: "unclassed",
				"moreLinkText"		: "READ MORE" 
			});

			galleryTitleBullets();
		}
	}

	function appViewLarge(){
		//DOC make homepage apps collapsible for small mobile devices
		$(".hp.two div.ui-widget-header").click(function(){
			var clickedApp = $(this).parent().parent().attr("id");

			$(".hp.two div.ui-widget.app").removeClass("active");
			$(this).parent().addClass("active");

			$("html, body").animate({
				scrollTop: $("#" + clickedApp).offset().top
			}, 500);
		});
	}

	function appViewSmall(){
		//DOC make homepage apps collapsible for small mobile devices
		$(".hp.two div.ui-widget-header, .hp-col-one-inner.right div.ui-widget-header").click(function(){
			var clickedApp = $(this).parent().parent().attr("id");

			$(".hp.two div.ui-widget.app, .hp-col-one-inner.right div.ui-widget.app").removeClass("active");
			$(this).parent().addClass("active");

			$("html, body").animate({
				scrollTop: $("#" + clickedApp).offset().top
			}, 500);
		});
	}

	function useChannelForSection(){
		//DOC use channel name for pagelistnavigation header if one is not present
		if(!$("div.sp.column.one .ui-widget-header h1").length > 0) {
			$("div.sp.column.one .ui-widget-header").append("
");
		}
        }

	function dynamicContent(){
		// ********** MYSTART ********** //
		//DOC replace mystart dropdown arrows
		$(".sw-myaccount-chevron").text("▼");
		$(".sw-dropdown-arrow").text("▼");
		$("#sw-mystart-search").clone().appendTo($("#gb-search"));
		$("#sw-mystart-search").clone().appendTo($("#gb-rs-search"));
		$("#gb-rs-search #sw-mystart-search").attr("id", "rs-menu-search").addClass("ui-clear");
		$("#gb-search #sw-mystart-search").show();

		// ********** HEADER ********** //
		var siteName = "Queensbury Union Free School District";
		siteName = siteName.split(" ");
		var siteNameLength = siteName.length;
		if(siteNameLength > 2){
			siteNameEnd = jQuery.trim(siteName.splice(-2, siteName.length).toString().replace(/,/g, " "));
		} else {
			siteNameEnd = jQuery.trim(siteName.splice(-1, siteName.length).toString().replace(/,/g, " "));
		}
		siteNameBegin = jQuery.trim(siteName.toString().replace(/,/g, " "));
		$("#gb-sitename").prepend("
" + siteNameEnd + "");
		$("#gb-sitename").prepend("
" + siteNameBegin + "");

		// ********** CHANNEL BAR ********** //
		//DOC add first class to first channel item
		$("li.sw-channel-item").first().addClass("first");
		//DOC add last class to last channel item
		$("li.sw-channel-item").last().addClass("last");

		// ********** BODY ********** //
		$("body").viewFullSite({
			"breakPoint"		: 767, // SYSTEM BREAK POINTS - 1023, 767, 639, 479
			"buttonContainer"	: "body",
			"useDefaultCSS"		: "yes",
			"fixedPosition"		: "bottom" // IF USING DEFAULT STYLES - TOP, BOTTOM
		});


		//DOC rename first breadcrumb
		$("li.ui-breadcrumb-first > a > span").text("Home");
		$("ul.ui-breadcrumbs > li").not($("ul.ui-breadcrumbs > li.ui-breadcrumb-last")).append(" / ");
		//DOC replace more-link text
		$("a.more-link span").text("More »");
		//DOC auto-focus username signin field
		$("#swsignin-txt-username").focus();
		//DOC center signed out message and signin button
        	if($("div.ui-spn > div > p > span").text() == "You have been signed out."){
        		$("div.ui-spn > div").css({"text-align" : "center", "padding" : "50px 0px 50px 0px"});
        		//DOC add signed out breadcrumb
        		$(".ui-breadcrumbs").append("
Signed Out");
        	}
        	//DOC add sign in breadcrumb
        	if($("#swlogin").length){
        		$(".ui-breadcrumbs").append("
Sign In");
        	}
        	if($(".app-level-social-rss").length){
        		$(".app-level-social-rss a span").append("Subscribe to Our NewsFeed");
        	}
        	$("#hp-slideshow .gallery-desc").prepend('');

		// ********** FOOTER ********** //
		//DOC get Schoolwires footer links
		var feedbackURL = $("div#sw-footer-links li:eq(0) a").attr("href");
		var termsURL = $("div#sw-footer-links li:eq(2) a").attr("href");
		var policyURL = $("div#sw-footer-links li:eq(1) a").attr("href");

		$("a#feedback").attr("href", feedbackURL);
		$("a#terms").attr("href", termsURL);
		$("a#policy").attr("href", policyURL);			
	}

	function searchText() {
		//DOC custom mystart search placeholder
	        //SEARCH TEXT
	        $("div#sw-mystart-search input#sw-search-input").removeAttr("placeholder");
		$("div#sw-mystart-search input#sw-search-input").attr("value", "Search Our Site...");
		$("div#sw-mystart-search input#sw-search-input").focus(function() {
			if($(this).val() == "Search Our Site...") {
				$(this).val("");
			}
		});
		$("div#sw-mystart-search input#sw-search-input").blur(function() {
			if($(this).val() == "") {
				$(this).val("Search Our Site...");
			}
		});
	}

	function calendarListView(){
		//DOC auto click function for schoolwires calendar list view for small mobile devices
		if(!$("a.ui-btn-toggle.list").hasClass("active")){
			$("a.ui-btn-toggle.list").click();
		}
	}

	function imgResize(){
		var imgHeight = $("#hp-slideshow .rotating-images img").height();
		$("#hp-slideshow, #hp-slideshow .rotating-images").css("height", imgHeight);
	}

	function galleryTitleBullets(){
		$("#hp-slideshow .gallery-container li.itemized.bullet").each(function(i){
			var myID = $("#sw-content-container10.region.ui-hp div.ui-widget.app.gallery").parent().parent().attr("id").replace("pmi-", "");
            		var myJSON = eval("photoGallery" + myID);
            		var totalFiles = myJSON.Album.Images.length;

            		if(i == 0){
            			$(this).addClass("first");
            		}
            		if(i == totalFiles - 1){
            			$(this).addClass("last");
            		}

            		$(this).append("" + myJSON.Album.Images[i].photoname + "");
		});
	}

	function tabCustomization(){
        	if ($("#hp-tabs .upcomingevents").length) {
   			$("#hp-tabs .upcomingevents").removeAttr("style");
   		}

   		var tabNum = $(".tab-button").length;
   		var tabWidth = 100 / tabNum;
   		$(".tab-button").css("width", tabWidth + "%");

   		$(".tab-button").first().addClass("first");
   		$(".tab-button").last().addClass("last");
   	}

	function modEvents(){
		$(".upcomingevents").modEvents({
			columns:"yes"
		});
		addDateBlocks();
	}	

	function addDateBlocks(){
		if($("div.ui-hp div.upcomingevents span.sw-calendar-block-title").length){
			$("div.ui-hp div.upcomingevents div.ui-article p.ui-article-description").not("div.ui-article.upcoming-column-container p.ui-article-description").each(function(){
				$(this).wrap("
");
				$(this).parent().parent().addClass("ui-clear upcoming-column-container");
				var monthText = $(this).parent().parent().parent().prev().find("div.upcoming-column.left").html();
				$(this).parent().parent().prepend("
" + monthText + "");
			});
		}

	}

	function sameHeights(){
		var homeHeight = $("#hp-content").height();
		$(".hp.column").css("height", homeHeight);

		var slideshowHeight = (homeHeight - $("#hp-slideshow").height()) - 10;
		$("#hp-col-one-columns").css("height", slideshowHeight);

		$(".hp.column .ui-widget.app .ui-widget-detail, #hp-slideshow").resize(function(){
			$(".hp.column, #hp-col-one-columns").css("height", "auto");

			var homeHeight = $("#hp-content").height();
			$(".hp.column").css("height", homeHeight);

			var slideshowHeight = (homeHeight - $("#hp-slideshow").height()) - 10;
			$("#hp-col-one-columns").css("height", slideshowHeight);
		});

	}

	function sameHeightResize(){
		$(".hp.column, #hp-col-one-columns").css("height", "auto");

		var homeHeight = $("#hp-content").height();
		$(".hp.column").css("height", homeHeight);

		var slideshowHeight = (homeHeight - $("#hp-slideshow").height()) - 10;
		$("#hp-col-one-columns").css("height", slideshowHeight);
	}

	function bulletResize(){
		if($(window).width() >= 1024) {
			bulletResizeLarge();
		}
		if($(window).width() >= 640 && $(window).width() < 1024) {
			bulletResizeSmall();
		}
	}

	function bulletResizeLarge(){
		$("#hp-slideshow .gallery-container li.itemized, #hp-slideshow .gallery-container li.itemized span").height("auto");
		$("#hp-slideshow .gallery-container li.itemized").eq(4).show();
		$("#hp-slideshow .gallery-container li.itemized").eq(3).removeClass("last");

		var bulletHeight = 0;
		bulletHeight += $("#hp-slideshow .gallery-container .itemize-container-outer").height() / 5;
		bulletHeight = bulletHeight - 22;

		$("#hp-slideshow .gallery-container li.itemized, #hp-slideshow .gallery-container li.itemized span").height(bulletHeight);
	}

	function bulletResizeSmall(){
		$("#hp-slideshow .gallery-container li.itemized, #hp-slideshow .gallery-container li.itemized span").height("auto");
		$("#hp-slideshow .gallery-container li.itemized").eq(4).hide();
		$("#hp-slideshow .gallery-container li.itemized").eq(3).addClass("last");

		var bulletHeight = 0;
		bulletHeight += $("#hp-slideshow .gallery-container .itemize-container-outer").height() / 4;
		bulletHeight = bulletHeight - 12;

		$("#hp-slideshow .gallery-container li.itemized, #hp-slideshow .gallery-container li.itemized span").height(bulletHeight);	
	}

	function compileNav(){
		if (!$("#gb-rs-menu-dropdown #gb-rs-menu-channels li").length) {
			// GRAB ALL OF THE CHANNELS AND DROPDOWNS
			$("li.sw-channel-item").each(function (){
				$("#gb-rs-menu-dropdown #gb-rs-menu-channels").append("
" + $(this).html() + "");
				var navDropdown = $(this).find("ul.sw-channel-dropdown");
				if ($(navDropdown).length) {
					$("#gb-rs-menu-dropdown #gb-rs-menu-channels > li").last().append("

");
				}
				$("#gb-rs-menu-dropdown #gb-rs-menu-channels .sw-channel-dropdown").removeClass("sw-channel-dropdown").addClass("gb-rs-channel-sublist");
			});

			// EXPAND THE MENU
			$("#gb-rs-menu-btn").click(function(){
				if (!$(this).hasClass("open")) {
					if($("#gb-rs-search-btn").hasClass("open")){
						$("#gb-rs-search").slideUp(function(){
							$("#gb-rs-search-btn").removeClass("open").addClass("closed");
							$("#gb-rs-menu-dropdown").slideDown(function(){
								$("#gb-rs-menu-btn").removeClass("closed").addClass("open");
							});
						});
					} else {
						$("#gb-rs-menu-dropdown").slideDown(function(){
							$("#gb-rs-menu-btn").removeClass("closed").addClass("open");
						});
					}
				} else {
					$("#gb-rs-menu-dropdown").slideUp(function(){
						$("#gb-rs-menu-btn").removeClass("open").addClass("closed");
					});
				}
			});

			// EXPAND AN INNER MENU ITEM 
			$(".gb-rs-list-bullet").click(function(){
				var clickedListItem = $(this);
				var subListBody = $(this).prev($(".gb-rs-channel-sublist"));
				if(!$(this).hasClass("open")){
					$(".gb-rs-list-bullet").removeClass("open").addClass("closed");
					$(".gb-rs-channel-sublist").slideUp(function(){
						clickedListItem.removeClass("closed").addClass("open");
					});
					subListBody.slideDown();
				} else {
					clickedListItem.removeClass("open").addClass("closed");
					subListBody.slideUp();	
				}
				$(".gb-rs-list-bullet").css("transform", "rotate(45 deg)");
			});
		}
	}

	function responsiveSearch(){
		$("#gb-rs-search-btn").click(function(){
			if(!$(this).hasClass("open")){
				if($("#gb-rs-menu-btn").hasClass("open")){
					$("#gb-rs-menu-dropdown").slideUp(function(){
						$("#gb-rs-menu-btn").removeClass("open").addClass("closed");
						$("#gb-rs-search").slideDown(function(){
							$("#gb-rs-search-btn").removeClass("closed").addClass("open");
						});
					});
				} else {
					$("#gb-rs-search").slideDown(function(){
						$("#gb-rs-search-btn").removeClass("closed").addClass("open");
					});
				}
			} else {
				$("#gb-rs-search").slideUp(function(){
					$("#gb-rs-search-btn").removeClass("open").addClass("closed");
				});
			}
		});
	}

