$(document).ready(function(){
   var beaconURL='https://analytics.schoolwires.com/analytics.asmx/Insert?AccountNumber=IoR%2bpJ1ZUaw7rpup6pn27g%3d%3d&SessionID=2461a5cc-81f5-4623-8da7-37a838e4488e&SiteID=4&ChannelID=0&SectionID=0&PageID=1&HitDate=4%2f20%2f2020+1%3a15%3a29+AM&Browser=Unknown+0.0&OS=Unknown&IPAddress=10.61.83.104';
