	$(window).load(function(){ 
		searchText();
		if($("#gb-page.gastoncountyschools").length){
			headlineImageCheck();
		}

	});

	$(function(){
		$(window).resize(function(){ 
			jsMediaQueries();
			headlinesSliderSizes();
		});

			jsMediaQueries();
			dynamicContent();
        	appAccordion();
        	pageListAccordion();
        	useChannelForSection();
        	modEvents();
        	alertMessage();
        	mmRotator();

        	if($("#gb-page.gastoncountyschools").length){
        		district = true;
        		//HEADLINES SLIDER
				hfCounter = 0;
				slideInterval = "";
				slideContainer = $("#hp-content-bottom .headlines ul");
				//CLONE THE FIRST 3 RECORDS FOR CAROSEL EFFECT
				$('#hp-content-bottom .headlines li').slice(0, 3).clone().appendTo(slideContainer);
				numSlides ='';
				sliderWidth = '';
				slideWidth = '';
				slideHeight = '';
				//HEADLINES SLIDER
				headlinesSliderSizes();
        		headlinesSlider();

        	} else {
        		district = false;
        		subsiteHPContentBottom();
        	}

        	HPTopLinks();
       		spPhotoGallery();
            slm();
			loadIcons();
	});	

	function jsMediaQueries() {
		switch(csGetBreakPoint()) {
			case "desktop": 

			break;
			case "768":

			break;
			case "640":

			break;
			case "480":

			break;
			case "320":

			break;
		}
	}

	function HPTopLinks(){
		var moreVideos = "More Videos";
		var moreVideosToggle = true;
		var moreAnnouncements = "More Announcements";
		var moreAnnouncementsToggle = true;

		if(district == true){

			$(".hp.column.two .app:first").addClass("hp-video");
			$(".hp.column.two .app:last").addClass("hp-announcements");

    		if(moreVideosToggle == true){
    			$(".hp.column.two .app.hp-video .ui-widget-footer").prepend(moreVideos);
    		}

    		if(moreAnnouncementsToggle == true){
    			$(".hp.column.two").append(moreAnnouncements);
    		}
		} else {
			$(".hp.column.two .app:first").addClass("hp-announcements")

			if(moreAnnouncementsToggle == true){
    			$(".hp.column.two").append(moreAnnouncements);
    		}
		}
	}

	function alertMessage(){
		if($("#hp-announcements .announcements .ui-articles li").length){
			$("#hp-announcements").show();
		}	
	}

