/* ------------------------------   HP HEADLINES SCROLLER  ------------------------------ */
/* ------------------------------   ____________________   ------------------------------ */

	function headlineImageCheck(){
		$("#hp-content-bottom .headlines li").each(function(){
			if(!$(".ui-article-thumb", this).length){
				$(this).addClass("no-image");
                var headlineURL = $(this).find(".ui-article-title a").attr("href");
				$(".ui-article", this).prepend('');
			}
		})
	}

	function headlinesSliderSizes(){
		numSlides = $("#hp-content-bottom .headlines li").length;

		if($(".lt-1024").length){
			//ADD 10 FOR MARGIN BETWEEN LIs
			slideWidth = 300+30;
			sliderWidth = slideWidth * numSlides;

		} else {
			//ADD 10 FOR MARGIN BETWEEN LIs
			slideWidth = 375+10;
			sliderWidth = slideWidth * numSlides;
		}

		$(slideContainer).width(sliderWidth);
		//WHY IS THIS MINUS 30 (10 IS FOR THE MARGIN)
		$("#hp-content-bottom .headlines li").width(slideWidth-30);
		//$("#hp-content-bottom").height(slideHeight);

		$(slideContainer).css("left","0px");
		hfCounter = 0;
	}

	function headlinesSlider(){

			if(!$(".auto-slide").length){

				//ADD SLIDER CONTROLS
				$('
'+
					''+
				'').insertAfter("#hp-content-bottom .headlines .ui-widget-header");

				$('
'+
					''+
				'').insertAfter("#hp-content-bottom .headlines .ui-widget-detail");

				var moreLink = $("#hp-content-bottom .headlines a.more-link");

	            if(moreLink.length > 0){
	            	var moreLinkURL = $("#hp-content-bottom .headlines a.more-link").attr("href");
					$("#hp-content-bottom .headlines .ui-widget-footer").prepend("More News");
	            }

				$("#hp-content-bottom .headlines li").each(function(){
					$(this).find(".ui-article-title, .ui-article-description").wrapAll("
");
				})

				$(slideContainer).addClass("auto-slide");
              	$("#hp-content-bottom .headlines ul li a").attr("tabindex", "-1");
            	$("#hp-content-bottom .headlines ul li:eq("+hfCounter+") .ui-article-title a").attr("tabindex", "0");
            	$("#hp-content-bottom .headlines ul li:eq("+hfCounter+")").next().find(".ui-article-title a").attr("tabindex", "0");
            	$("#hp-content-bottom .headlines ul li:eq("+hfCounter+")").next().next().find(".ui-article-title a").attr("tabindex", "0");
				slideTimer();
			}


			//ANIMATE SLIDER ON NAV CONTROL CLICK
            $(".headline-slider-controls button").click(function(){
                if(!$("#hp-content-bottom").hasClass("animating")){
                  //STOP THE TIMER
                  clearInterval(slideInterval);
                  //IF THE PREVIOUS BUTTON IS CLICKED SLIDE TO THE RIGHT
                  if($(this).hasClass("previous")){
                      //ONLY SLIDE IF THE FIRST SLIDE IS NOT IN THE FIRST POSITION
                      if(hfCounter != 0){
                          $("#hp-content-bottom").addClass("animating");
                          $(slideContainer).animate({ left: '+='+slideWidth }, "slow", function() {
                              hfCounter--;
                              $("#hp-content-bottom .headlines ul li a").attr("tabindex", "-1");
                              $("#hp-content-bottom .headlines ul li:eq("+hfCounter+") .ui-article-title a").attr("tabindex", "0");
                              $("#hp-content-bottom .headlines ul li:eq("+hfCounter+")").next().find(".ui-article-title a").attr("tabindex", "0");
                              $("#hp-content-bottom .headlines ul li:eq("+hfCounter+")").next().next().find(".ui-article-title a").attr("tabindex", "0");
                              $("#hp-content-bottom").removeClass("animating");
                          });
                      } else {
                          var newLeft = (numSlides-3) * slideWidth;
                          $(slideContainer).css("left","-"+newLeft+"px");
                          hfCounter = numSlides-3;

                          $(slideContainer).animate({ left: '+='+slideWidth }, "slow", function() {
                              hfCounter--;
                              $("#hp-content-bottom .headlines ul li a").attr("tabindex", "-1");
                              $("#hp-content-bottom .headlines ul li:eq("+hfCounter+") .ui-article-title a").attr("tabindex", "0");
                              $("#hp-content-bottom .headlines ul li:eq("+hfCounter+")").next().find(".ui-article-title a").attr("tabindex", "0");
                              $("#hp-content-bottom .headlines ul li:eq("+hfCounter+")").next().next().find(".ui-article-title a").attr("tabindex", "0");
                              $("#hp-content-bottom").removeClass("animating");
                          });
                      }
                  } 

                  //IF THE NEXT BUTTON IS CLICKED, SLIDE TO THE LEFT
                  if($(this).hasClass("next")){
                      $("#hp-content-bottom").addClass("animating");
                      $(slideContainer).animate({ left: '-='+slideWidth }, "slow", function() {
                          hfCounter++;
                          $("#hp-content-bottom .headlines ul li a").attr("tabindex", "-1");
                          $("#hp-content-bottom .headlines ul li:eq("+hfCounter+") .ui-article-title a").attr("tabindex", "0");
                          $("#hp-content-bottom .headlines ul li:eq("+hfCounter+")").next().find(".ui-article-title a").attr("tabindex", "0");
                          $("#hp-content-bottom .headlines ul li:eq("+hfCounter+")").next().next().find(".ui-article-title a").attr("tabindex", "0");
                          //IF THE CLONED FIRST SLIDE IS IN THE FAR LEFT POSITION, RESET THE SLIDER FOR CAROSEL EFFECT
                          if(hfCounter == (numSlides -3) ){
                              $(slideContainer).css("left","0px");
                              hfCounter = 0;
                              $("#hp-content-bottom .headlines ul li a").attr("tabindex", "-1");
                              $("#hp-content-bottom .headlines ul li:eq("+hfCounter+") .ui-article-title a").attr("tabindex", "0");
                              $("#hp-content-bottom .headlines ul li:eq("+hfCounter+")").next().find(".ui-article-title a").attr("tabindex", "0");
                              $("#hp-content-bottom .headlines ul li:eq("+hfCounter+")").next().next().find(".ui-article-title a").attr("tabindex", "0");
                          } 
                          $("#hp-content-bottom").removeClass("animating");
                      }); 
                  } 

                  //RESTART THE TIMER
                  slideTimer();
                }      
            });

                //PAUSE ON HOVER
                $(slideContainer).hover(function(){
                //STOP THE SLIDER
                clearInterval(slideInterval);
                    }, function(){
                    slideTimer();
                });

                $("#hp-content-bottom .headlines li").hover(function(){
                    var slide = $(this);
                    $(slide).addClass("hover");

                    setTimeout(function(){
                        $(slide).addClass("visible");
                    }, 100);
                }, function(){
                    var slide = $(this);
                    $(slide).removeClass("visible");
                    setTimeout(function(){
                        $(slide).removeClass("hover");
                    }, 250);
                });
	}

	function slideHeadlines(){
    	$("#hp-content-bottom").addClass("animating");
		$(slideContainer).animate({ left: '-='+slideWidth }, "slow", function() {
			hfCounter++;
            $("#hp-content-bottom .headlines ul li a").attr("tabindex", "-1");
            $("#hp-content-bottom .headlines ul li:eq("+hfCounter+") .ui-article-title a").attr("tabindex", "0");
            $("#hp-content-bottom .headlines ul li:eq("+hfCounter+")").next().find(".ui-article-title a").attr("tabindex", "0");
            $("#hp-content-bottom .headlines ul li:eq("+hfCounter+")").next().next().find(".ui-article-title a").attr("tabindex", "0");
            $(".headline-slider-controls .previous").attr("tabindex","0");

			//IF THE CLONED FIRST SLIDE IS IN THE FAR LEFT POSITION, RESET THE SLIDER FOR CAROSEL EFFECT
			if(hfCounter == (numSlides -3) ){
				$(slideContainer).css("left","0px");
				hfCounter = 0;
				$(".headline-slider-controls .previous").attr("tabindex","-1");
			}


            $("#hp-content-bottom").removeClass("animating");
		});	

	}

	function slideTimer(){
		slideInterval = setInterval(slideHeadlines, 12000);
	}

	function subsiteHPContentBottom(){
		$("#hp-content-bottom .headlines").each(function(){
			var moreLink = $(this).find("a.more-link");

            if(moreLink.length > 0){
            	var moreLinkURL = $(this).find("a.more-link").attr("href");
				$(this).parent().parent().parent().append("More News");
            	$(this).find(".ui-widget-footer").prepend("More News");
            }
		})

		var calendarLink = "/Page/2";

		$("#hp-content-bottom .upcomingevents").each(function(){
			$(this).parent().parent().append("More Events");
			$(this).find(".ui-widget-footer").prepend("More Events");
        })

        $("#hp-content-bottom .announcements").each(function(){
			var moreLink = $(this).find("a.more-link");

            if(moreLink.length > 0){
            	var moreLinkURL = $(this).find("a.more-link").attr("href");
				$(this).parent().parent().parent().append("More Announcements");
				$(this).find(".ui-widget-footer").prepend("More Announcements");
            }
		})
	}

